import React, {useState, useEffect, useCallback} from 'react';
import { useForm, useController, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import { Table1 } from "./Table";
import { NiceInput } from "./Input"; 
 
let list = [];
const name = "shareinfo";

function ShareAgreement() {

    // const {
    //   register,
    //   reset,
    //   handleSubmit,
    //   setValue,
    //   control,
    // } = useForm();
    const [shareInfo, setShareInfo] = useState([]);
    const [founderInfo, setFounderInfo] = useState([]);
    const [corpInfo, setCorpInfo] = useState([]);
    const methods = useForm();
    const onSubmit = (data) => {
        console.log(JSON.stringify(data, null, 2));
    }
    
    useEffect(() => {
        methods.register({name: "shareinfo"})
    }, [methods])
    
    const addShareRow = function () {
        let l = shareInfo.length + 1;
        const d = [...shareInfo];


        const item = {
            refNum: l,
            shareName: "",
            shareClass: "",
            shareType: "",
            shareTotal: 0,
        };
        list.push(item.refNum);
        d.push(item);
        setShareInfo(d);
        
    }

    const removeShareRow = function(){
        let l = [...shareInfo];
        //l.splice(-1, 1);
        l.pop();
        
        if (list[0] != null) {
            list.pop();
        }
        //reset(`shareInfo[${-1}][shareName]`);
        //unregister(`shareinfo[${-1}][shareName]`);
        setShareInfo(l);
            
        };
    
    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
        Cell: ({ row: { index } }) => (
          <NiceInput
            type="text"
            id={`shareInfo[${index}][shareName]`}
            name={`shareInfo[${index}][shareName]`}
            control={methods.control}
            method={methods.register}
          />
        ),
      },

      {
        Header: "Share Type",
        columns: [
          {
            Header: "Class",
            accessor: "shareClass",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`shareInfo[${index}][shareClass]`}
                // innerRef={register}
              >
                <option>Class A</option>
                <option>Class B</option>
                <option>None</option>
              </Input>
            ),
          },
          {
            Header: "Type",
            accessor: "shareType",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`shareinfo[${index}][shareType]`}
                // innerRef={register}
              >
                <option>Common</option>
                <option>Preferred</option>
              </Input>
            ),
          },
        ],
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
        Cell: ({ row: { index } }) => (
          <Input
            type="number"
            name={`shareinfo[${index}][shareTotal]`}
            // innerRef={register}
          />
        ),
      },
    ];

    const addFounderRow = function () {
        let l = founderInfo.length + 1;
        const d = [...founderInfo];

        const item = {
        fname: "",
        refId: "",
        numHeld: 0,
        streetName: "",
        cityName: "",
        provinceName: "",
        postalCode: "",
        phone: "",
        email: "",
        };
        d.push(item);
        setValue("founderinfo", d);
        setFounderInfo(d);
    };

    // const founderColumns = [
    //   {
    //     Header: "Full Name",
    //     accessor: "fname",
    //     Cell: ({ row: { index } }) => (
    //       <NiceInput
    //         type="text"
    //         control={control}
    //         name={`founderinfo[${index}][fname]`}
    //         innerRef={register}
    //       />
    //     ),
    //   },

    //   {
    //     Header: "Shares Held",
    //     columns: [
    //       {
    //         Header: "Reference #",
    //         accessor: "refID",
    //       },

    //       {
    //         Header: "# Held",
    //         accessor: "numHeld",
    //         Cell: ({ row: { index } }) => (
    //           <Input
    //             type="number"
    //             name={`founderinfo[${index}][numHeld]`}
    //             innerRef={register}
    //           />
    //         ),
    //       },
    //     ],
    //   },

    //   {
    //     Header: "Address",
    //     columns: [
    //       {
    //         Header: "Street Name",
    //         accessor: "streetName",
    //         Cell: ({ row: { index } }) => (
    //           <Input
    //             type="text"
    //             name={`founderinfo[${index}][streetName]`}
    //             innerRef={register}
    //           />
    //         ),
    //       },
    //       {
    //         Header: "City",
    //         accessor: "cityName",
    //         Cell: ({ row: { index } }) => (
    //           <Input
    //             type="text"
    //             name={`founderinfo[${index}][cityName]`}
    //             innerRef={register}
    //           />
    //         ),
    //       },
    //       {
    //         Header: "Province",
    //         accessor: "provinceName",
    //         Cell: ({ row: { index } }) => (
    //           <Input
    //             type="select"
    //             name={`founderinfo[${index}][provinceName]`}
    //             innerRef={register}
    //           >
    //             <option value="AB">Alberta</option>
    //             <option value="BC">British Columbia</option>
    //             <option value="MB">Manitoba</option>
    //             <option value="NB">New Brunswick</option>
    //             <option value="NL">Newfoundland and Labrador</option>
    //             <option value="NS">Nova Scotia</option>
    //             <option value="ON">Ontario</option>
    //             <option value="PE">Prince Edward Island</option>
    //             <option value="QC">Quebec</option>
    //             <option value="SK">Saskatchewan</option>
    //             <option value="NT">Northwest Territories</option>
    //             <option value="NU">Nunavut</option>
    //             <option value="YT">Yukon</option>
    //           </Input>
    //         ),
    //       },
    //       {
    //         Header: "Postal Code",
    //         accessor: "postalCode",
    //         Cell: ({ row: { index } }) => (
    //           <Input
    //             type="text"
    //             name={`founderinfo[${index}][postalCode]`}
    //             innerRef={register}
    //           />
    //         ),
    //       },
    //     ],
    //   },

    //   {
    //     Header: "Phone Number",
    //     accessor: "phone",
    //     Cell: ({ row: { index } }) => (
    //       <Input
    //         type="tel"
    //         pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
    //         name={`founderinfo[${index}][phone]`}
    //         innerRef={register}
    //       />
    //     ),
    //   },

    //   {
    //     Header: "Email(Optional)",
    //     accessor: "email",
    //     Cell: ({ row: { index } }) => (
    //       <Input
    //         type="email"
    //         name={`founderinfo[${index}][email]`}
    //         innerRef={register}
    //       />
    //     ),
    //   },
    // ];
    return (
      <form onSubmit={methods.handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your company’s Name?</label>
          <Input
            type="text"
            name="companyName"
            innerRef={methods.control}
          />
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <NiceInput
            type="date"
            name="companyDate"
            innerRef={methods.control}
            //method={methods.register}
          />
        </FormGroup>
        <FormGroup>
          <Label for="table1">What Does Your Company Have?</Label>
          <Table1
            columns={shareColumns}
            ref={methods.register}
            data={shareInfo}
          />
          <Button onClick={addShareRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup>
        {/* <FormGroup>
          <Label for="table2">Who are the founders?</Label>
          <Table1 columns={founderColumns} data={founderInfo} />
          <Button onClick={addFounderRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup> */}
        <input type="submit" />
      </form>
    );
}

export default ShareAgreement;
