import React, { useState } from "react";
import { Row, Column } from "simple-flexbox";
import { createUseStyles, useTheme } from "react-jss";
import CardComponent from "../../components/cards/CardComponent";
import styled from 'styled-components';


const Grid = styled.div`

`;

const useStyles = createUseStyles((theme) => ({

  itemTitle: {
    ...theme.typography.itemTitle,
    color: theme.color.veryDarkGrayishBlue,
    
    
  },
  itemDate: {
    ...theme.typography.itemTitle,
    color: theme.color.veryDarkGrayishBlue,
    alignItems: "right"
  },
  itemValue: {
    color: theme.color.grayishBlue2,
  },
  greyTitle: {
    color: theme.color.grayishBlue3,
  },
  tagContainer: {
    alignItems: "center",
    margin: "center",
  }, 
  tagStyles: {
    borderRadius: 20,
    fontWeight: "bold",
    fontSize: 11,
    letterSpacing: "0.5px",
    lineHeight: "14px",
    alignItems: "center",
    padding: "5px 15px 5px 15px",
  },
}));

const TAGS = {
  URGENT: { text: "UNSIGNED", backgroundColor: "#FEC400", color: "#FFFFFF" },
  NEW: { text: "SIGNED", backgroundColor: "#29CC97", color: "#FFFFFF" },
  DEFAULT: { text: "DRAFTED", backgroundColor: "#F0F1F7", color: "#9FA2B4" },
};

function TasksComponent(props, userData) {
  const theme = useTheme();
  const classes = useStyles({ theme });
  const [items, setItems] = useState(
    [
    // { title: "Share Agreement", date: "Today", tag: TAGS.URGENT },
    // {
    //   title: "Name Agreement",
    //   date: "Tomorrow",
    //   tag: TAGS.NEW,
    // },
    // {
    //   title: "Agreement 02",
    //   date: "Tor", 
    //   tag: TAGS.DEFAULT
    // },
    ]
  );

  function getNextTag(current = "DRAFTED") {
    const tagLabels = ["UNSIGNED", "SIGNED", "DRAFTED"];
    const tagIndex = (tagLabels.indexOf(current) + 1) % 3;
    return TAGS[tagLabels[tagIndex]];
  }

  // function onAddButtonClick() {
  //   setItems((prev) => {
  //     const newItems = [...prev];
  //     newItems.push({
  //       title: `Agreement ${newItems.length + 1}`,
  //       tag: getNextTag(),
  //     });
  //     return newItems;
  //   });
  // }

  return (
    <CardComponent
      containerStyles={props.containerStyles}
      items={[
        // <Row horizontal="space-between" vertical="center">
        //   <span className={[classes.itemTitle, classes.greyTitle].join(" ")}>
        //     Create new task
        //   </span>
        //   {renderAddButton()}
        // </Row>,
        ...items.map((item, index) => (
          <TaskComponent
            classes={classes}
            index={index}
            item={item}
          />
        )),
      ]}
    />
  );
}

function TaskComponent({
  classes,
  item = {},
}) {
  const { tag = {} } = item;
  return (
    <Grid>
      <Row flex-flow="row wrap" horizontal="space-between" center="center">
        <Column xs="true">
          <span className={classes.itemTitle}>{item.title}</span>
        </Column>
        <Column xs="true">
          <div className={classes.tagContainer} vertical="center">
            <TagComponent
              backgroundColor={tag.backgroundColor}
              classes={classes}
              color={tag.color}
              text={tag.text}
            />
          </div>
        </Column>
        <Column xs="true">
          <Row horizontal="space-between" vertical="start">
            <div className={classes.itemDate}>{item.date}</div>
          </Row>
        </Column>
      </Row>
    </Grid>
  );
}

function TagComponent({
  backgroundColor,
  classes,
  color,
  text,
}) {
  return (
    <Row
      horizontal="center"
      vertical="center"
      style={{ backgroundColor, color }}
      className={classes.tagStyles}
    >
      {text}
    </Row>
  );
}

export default TasksComponent;
