//   });

//   return <Input {...inputProps} />;
// }

export const NiceInput = React.forwardRef(({ type, name, method}, ref) =>
{
    // const {
    //   field: { refs, ...inputProps },
    //   fieldState: { invalid, isTouched, isDirty },
    //   formState: { touchedFields, dirtyFields },
    // } = useController({
    //   name,
    //   control,
    //   rules: { required: true },
    //   defaultValue: "",
    // });

    return (
      <Input
        name={name}
        type={type}
        innerRef={method}
      />
    );

});

export const SelectNiceInput = React.forwardRef(({ control, name, options }) => {
  const {
    field: { refs, ...inputProps },
    meta: { invalid, isTouched, isDirty },
  } = useController({
    name,
    control,
    rules: { required: true },
    defaultValue: "",
  });

  return (<Input
    {...inputProps}
    type="select"
    ref={refs}>
    {options.map(value => (
      <options key={value} value={value}>
        {value}
      </options>
    ))}
  </Input>
  );
});
