import { useState} from "react";
import axios from "axios";

const useEmail = () => {

  const [fields, setValues] = useState({
    email: "",
  });

  const [error, setErrors] = useState({});
  const [IsSubmitting, setIsSubmitting] = useState({})
  const [response, setResponse] = useState({
    error: false,
    message: "",
  });
    
  const [success, setSuccess] = useState({
    state: false,
    message: "",
  });

  const handleEmailChange = (e) => {
    const { name, value } = e.target;
    setValues({
      ...fields,
      [name]: value,
    });
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;

    const validation = validator.errors

    if (value === "") {
      return;
    }

     validator.validate(name, value).then(() => {
       if (validation.has(name)) {
         const errors = fields
         errors[name] = validation.first(name); 
         setErrors(errors);
       }
     });


  }

  const handleSubmit = (e) => {
    e.preventDefault();

    const data = fields;
    axios
      .post("http://127.0.0.1:8000/api/password/email", data)
      .then((res) => {
          const success = {
              state: true,
              message: res.message
          };
          setSuccess(success); 
      })
      .catch((err) => {
        const errors = err.response.data; 
        const response = {
          error: true,
          message: errors,
        };
        console.log(response)
        //setResponse(response);
      });
      
  };

  return { handleEmailChange, fields, handleSubmit, handleBlur, response, success };
};

export default useEmail;
