import React, { useState } from "react";
import { Row, Column } from "simple-flexbox";
import { createUseStyles, useTheme } from "react-jss";
import CardComponent from "../../components/cards/CardComponent";
import styled from "styled-components";
import { useHistory } from "react-router-dom";
import SLUGS from "../../resources/slugs";
import { convertSlugUrl } from "../../resources/utils";

const Grid = styled.div``;

const useStyles = createUseStyles((theme) => ({
  itemTitle: {
    ...theme.typography.itemTitle,
    color: theme.color.veryDarkGrayishBlue,
  },
  itemDate: {
    ...theme.typography.itemTitle,
    color: theme.color.veryDarkGrayishBlue,
    alignItems: "right",
    textAlign: "right",
  },
  itemValue: {
    color: theme.color.grayishBlue2,
  },
  greyTitle: {
    color: theme.color.grayishBlue3,
  },
  tagContainer: {
    alignItems: "center",
    margin: "center",
  },
  tagStyles: {
    borderRadius: 20,
    fontWeight: "bold",
    fontSize: 11,
    letterSpacing: "0.5px",
    lineHeight: "14px",
    alignItems: "center",
    float: "left",
    padding: "5px 15px 5px 15px",
  },
}));

const TAGS = {
  UNSIGNED: { text: "UNSIGNED", backgroundColor: "#FEC400", color: "#FFFFFF" },
  SIGNED: { text: "SIGNED", backgroundColor: "#29CC97", color: "#FFFFFF" },
  UNDRAFTED: { text: "DRAFTED", backgroundColor: "#F0F1F7", color: "#9FA2B4" },
};

function getNextTag(current) {
  return TAGS[current];
}

function TasksComponent({ items, containerStyles }) {
  const theme = useTheme();
  const { push } = useHistory();
  const classes = useStyles({ theme });
  const dataItems = [];

  items.map((x, i) => {
    return (dataItems[i] = {
      id: x.id,
      user_id: x.owner,
      name: x.name,
      tag: getNextTag(x.status),
      date: x.created_at,
    });
  });

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  return dataItems ? (
    dataItems.map((item, index) => (
      <CardComponent
        key={index}
        containerStyles={containerStyles}
        items={[
          <TaskComponent
            onClick={() => onClick(SLUGS.createShare + item.id)}
            classes={classes}
            index={index}
            item={item}
          />,
        ]}
      />
    ))
  ) : (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <p> Populating... </p>
    </div>
  );
}

function TaskComponent({ classes, item = {}, onClick }) {
  const { tag = {} } = item;
  return (
    <Row horizontal="space-between" center="center" onClick={onClick}>
      <span className={[classes.itemTitle, "titleCard"].join(" ")}>
        {item.name}
      </span>
      <div className={classes.tagContainer} vertical="center">
        <TagComponent
          backgroundColor={tag.backgroundColor}
          classes={classes}
          color={tag.color}
          text={tag.text}
        />
      </div>
      <div className={classes.itemDate}>{item.date}</div>
    </Row>
  );
}

function TagComponent({ backgroundColor, classes, color, text }) {
  return (
    <Row
      horizontal="center"
      vertical="center"
      style={{ backgroundColor, color }}
      className={classes.tagStyles}
    >
      {text}
    </Row>
  );
}

export default TasksComponent;
