import React, {useState, useEffect, useCallback} from 'react';
import { useForm, FormProvider, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import  styled from "styled-components"; 
import { SharesTable, FoundersTable } from "./Table";
import "./test.css"; 
 
const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      margin: 0;
      padding: 0.5rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }

      input {
        font-size: 1rem;
        padding: 0;
        margin: 0;
        border: 0;
      }
    }
  }

  .pagination {
    padding: 0.5rem;
  }
`;

function ShareAgreement() {
    const [shareInfo, setShareInfo] = useState([]);
    const [founderInfo, setFounderInfo] = useState([]);
    const [corpInfo, setCorpInfo] = useState([]);
    const methods = useForm();
    const onSubmit = (data) => {
        console.log(JSON.stringify(data, null, 2));
    }

    const addShareRow = function () {
        let l = shareInfo.length + 1;
        const d = [...shareInfo];
        
            const item = {
            refNum: l,
            shareName: "",
            shareClass: "",
            shareType: "",
            shareTotal: 0,
        };
        d.push(item);
        setShareInfo(d);   
    }

    const removeShareRow = function(){
        let l = [...shareInfo];
        l.pop();
        setShareInfo(l);
            
    };

    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
      },

      {
        Header: "Share Type",
        columns: [
          {
            Header: "Class",
            accessor: "shareClass",

          },
          {
            Header: "Type",
            accessor: "shareType",
          },
        ],
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
      },
    ];

    const addFounderRow = function () {
        let l = founderInfo.length + 1;
        const d = [...founderInfo];

        const item = {
        fname: "",
        role: "",
        refId: "",
        numHeld: 0,
        addressInfo: "", 
        phone: "",
        email: "",
        };
        d.push(item); 
        setFounderInfo(d);
    };

    const removeFounderRow = function () {
      let l = [...founderInfo];
      l.pop();
      setFounderInfo(l);
    };

    const founderColumns = [
      {
        Header: "Full Name",
        accessor: "fname",

        },
        {
            Header: "Role",
            accessor: "role",
        },

        {
            Header: "Shares Held",
            columns: [
                {
                    Header: "Reference #",
                    accessor: "refID",
                },

                {
                    Header: "# Held",
                    accessor: "numHeld",

                },
            ],
        },
      {
        Header: "Address",
        accessor: "addressInfo"
      },

      {
        Header: "Phone Number",
        accessor: "phone",
      },

      {
        Header: "Email(Optional)",
        accessor: "email",
      },
    ];

    return (
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <FormGroup>
            <label>What is your companyâ€™s Name?</label>
            <Input type="text" name="companyName" innerRef={methods.register} />
          </FormGroup>
          <FormGroup>
            <label>When was your company incorporated?</label>
            <Input
              type="date"
              name="companyDate"
              innerRef={methods.register}
              //method={methods.register}
            />
          </FormGroup>

          <FormGroup>
            <Label for="sTable">What Does Your Company Have?</Label>
            <div className="Table">
              <Controller
                name="sTable"
                control={methods.control}
                defaultValue="name"
                as={
                <Styles>
                  <SharesTable
                    name="sTable"
                    columns={shareColumns}
                    ref={methods.register}
                    data={shareInfo}
                    methods={methods}
                  />
                </Styles>
                }
              />
            </div>
            <Button onClick={addShareRow}>Add Row </Button>{" "}
            <Button onClick={removeShareRow}>Remove Row</Button>
          </FormGroup>
          <FormGroup>
            <Label>Who are the founders?</Label>
            <div className="Table">
            <Styles>
            <FoundersTable
              columns={founderColumns}
              data={founderInfo}
              ref={methods.register}
              methods={methods}
            />
            </Styles>
            </div>
            <Button onClick={addFounderRow}>Add Row </Button>{" "}
            <Button onClick={removeFounderRow}>Remove Row</Button>
          </FormGroup>
          <input type="submit" />
        </form>
      </FormProvider>
    );
}

export default ShareAgreement;
