import React, { useState, useEffect }from "react";
import { useTable } from "react-table";
import { Input, Label, Button, Row, Col } from "reactstrap";
import "./test.css";

export const SharesTable = React.forwardRef(({ columns, data, methods}, ref) => {
  // Use the state and functions returned from useTable to build your UI
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data,
    });

  // Render the UI for your table
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>{i + 1}</td>
                <td>
                  <Input
                    type="text"
                    name={`shareInfo[${i}][shareName]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareClass]`}
                    innerRef={methods.register}
                  >
                    <option>Class A</option>
                    <option>Class B</option>
                    <option>None</option>
                  </Input>
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareType]`}
                    innerRef={methods.register}
                  >
                    <option>Common</option>
                    <option>Preferred</option>
                  </Input>
                </td>

                <td>
                  <Input
                    type="number"
                    name={`shareInfo[${i}][shareTotal]`}
                    innerRef={methods.register}
                  />
                </td>
              </tr>
            );
        })}
      </tbody>
    </table>
  );
});

let i = 0;
export const FoundersTable = React.forwardRef(
  ({ columns, data, methods, addRow}, ref) => {
    // Use the state and functions returned from useTable to build your UI
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
      useTable({
        columns,
        data,
      });

    // Render the UI for your table
    // const [person, setPerson] = useState({
    //   refNum: ""

    // });

    const [inputList, setInputList] = useState([
      {
        index: [{ name: `shareinfo[${i}]`}]
      },
    ]);
    
    const handleInput = (e, i) => {
      const { name, value } = e.target;
      const list = [...inputList]
      list[addRow][i][name] = value;
      setInputList(list);
      console.log(inputList)
    }

    const handleAddClick = () => {
      i += 1;
      const d = [...inputList]; 
      const item = [
        {
          name: `shareinfo[${i}]`,
        },
      ];
      d.push(item)
      setInputList(d); 
    }

    const handleRemoveClick = index => {
      i -= 1; 
      const list = [...inputList]
      list.splice(index, 1)
      setInputList(list)
    }


    return (
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>{i + 1}</td>
                <td colSpan="2">
                  {inputList[addRow].map((x, index) => {
                    return (
                      <div>
                        <Row>
                          <Col>
                            <Input
                              name={`founderInfo[${i}][${x.name}][refID]`}
                              placeholder="Enter First Name"
                              //value={x.refID}
                              onChange={(e) => handleInput(e, index)}
                              innerRef={methods.register}
                            />
                          </Col>
                          <Col>
                            <Input
                              name={`founderInfo[${i}][${x.name}][numHeld]`}
                              placeholder="No. Shares held"
                              type="number"
                              onChange={(e) => handleInput(e, index)}
                              innerRef={methods.register}
                            />
                          </Col>
                        </Row>
                        <div className="btn-box">
                          {inputList.length !== 1 && (
                            <button
                              className="mr10"
                              onClick={() => handleRemoveClick(index)}
                            >
                              Remove
                            </button>
                          )}
                          {inputList.length - 1 === index && (
                            <button onClick={handleAddClick}>Add</button>
                          )}
                        </div>
                      </div>
                    );
                  })}
                  
