import React, { useEffect, useState} from "react";
import { Column, Row } from "simple-flexbox";
import CreateButton from "../../components/CreateButton";
import axios from "axios";
import { useParams } from "react-router";
import { Page, Text, View, Document, StyleSheet} from "@react-pdf/renderer";

const styles = StyleSheet.create({
  page: {
    marginTop: 80,
    flexDirection: "row",
    backgroundColor: "#FFFFFF",
  },
  section: {
    margin: 10,
    padding: 10,
    flexGrow: 1,
  },
  lastRow: {
    paddingTop: 40,
    marginTop: 30,
    //maginBottom: 40
  },
});



export function CreateShareAgreement() {

  const { id } = useParams();

  // const { corpName, corpPlace, corpDate } = "";
  // const { shareData, personData } = [];

  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [data, setData] = useState([]);
  useEffect(() => {
    getData();
  }, [])

  const getData = async () => {
    const d = await axios
      .get(api, config)
      .then((res) => {
        setData(res.data[0].data);
        console.log(JSON.stringify(data));
      })
    
  }

  return (
    <Column>
        <Row horizontal="end" className={styles.lastRow}>
          <CreateButton
          label="Download"
          />
      </Row>
      <Row
        horizontal="space-between"
        className={styles.lastRow}
      >
        {/* <DisplayData
          data={data}
        /> */}
      </Row>

    </Column>
  );
}

function DisplayData({ data } )
{

    const val = data;
    const corpName = val.companyName;
    const corpPlace = val.companyPlace;
    const corpDate = val.companyDate;
    const shareData = [];
    const personData = [];
    
    val.shareInfo.map((x, i) => {
      return (shareData[i] = {
        shareID: x.shareID,
        shareName: x.shareName,
        shareClass: x.shareClass,
        shareType: x.shareType,
        shareTotal: x.shareTotal
      });
    });

    val.founderInfo.map((x, i) => {
      return (personData[i] = {
        personID: x.personID,
        name: [x.fname, x.preferredName],
        phone: x.phone,
        email: x.email,
        address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
        IsCompany: [x.role, x.corpName],
        Ownership: x.shareinfo,
        IsBoard: x.onBoard,
        
      });
    });
  
  const Beginning = function() {
      
      const d = "";
      
      return (
        <Text>
          THIS AGREEMENT (the “Agreement”), is made as of {corpDate},
          between {corpName}, a corporation incorporated under the laws
          of {corpPlace} (the “Corporation”);
          {personData.map((x) => {
              return `
                ${x.name[0]},
                an individual residing in the City of
                ${x.address[2]},
                ${
                  !x.name[1]
                    ? `${x.address[1]};`
                    : `${x.address[1]} ("${x.name[1]}");`
                } `;
          })}
          each investor being listed on Schedule “A” annexed hereto, and
          any person who becomes a party hereto by executing an
          acknowledgement in the form annexed hereto as Schedule “B”.
        </Text>
      );
  }
  // console.log(beginning())

  return (
    <Row className={styles.lastRow}>
      <Document>
        <Page size="A4" style={styles.page}>
          <View style={styles.section}>
            <Beginning />
          </View>
        </Page>
      </Document>
    </Row>
  );
};
