import React, {useState, useEffect} from "react"; 
import axios from "axios"; 
import {useHistory} from "react-router-dom"; 

export default function TicketDashboard() 
{
    const {push} = useHistory();
    const [data, setData] = useState();
    const [loading, setLoading] = useState(); 

    useEffect(() => {
        getData(); 
    }, []); 

    const token = sessionStorage.getItem("xrsf");
    const config = {
      headers: { 'Authorization': `Bearer ${token}` },
    };

    const api = `${process.env.REACT_APP_API}/api/tickets`

    const getData = async () =>{
        setLoading(true);
        await axios
        .get(api, config)
        .then((res) => {
            console.log(res.data)
            setData(res.data); 
            setLoading(false);
        })   
    }

    return(
        <div>
        <Row horizontal="end" className={classes.buttonRow}>
            <CreateButton
            label="Create Agreement"
            onClick={() => onClick(SLUGS.selectTemplate)}
            />
        </Row>
        <Column className={classes.lastRow}> 
            <TicketComponent
                data={data}
                loading={loading}
            />
        </Column>
        </div>
    ); 

}
