import React, {useEffect, useState} from "react";
import { Column, Row } from "simple-flexbox";
import axios from "axios"; 
import { createUseStyles } from "react-jss";
import TasksComponent from "./AgreementComponent";
import CreateButton from "../../components/CreateButton"; 
import SLUGS from "../../resources/slugs";
import { convertSlugUrl } from "../../resources/utils";
import { useHistory } from "react-router-dom";

const useStyles = createUseStyles({
    buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },
  tasks: {
    marginTop: 0,
    "@media (max-width: 1024px)": {
      marginTop: 30,
    },
  },
  buttonStyle: {
    display: "flex",
    justifyContent: "flex-end",
  },
});



function DashboardComponent() {

  const currUser = "http://localhost:8000/api/user";

  const token = localStorage.getItem("token"); 

  const config = {
    headers: {Authorization: `Bearer ${token}`}
  }
  const [userData, setUserData] = useState([]);
  const { push } = useHistory();
  
  useEffect(() => {
    getCurrentUser();
  }, []);

  const getCurrentUser = async () => {
    const data = await axios.get(currUser,
      config)
      .then(res => {
        console.log(res);
        setUserData(res.data); 
      })
      .catch(err => {
        console.log(err); 
    })
  };

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  const classes = useStyles();
  return (
    <Column>
      <Row
        horizontal="end"
        className={classes.buttonRow}
      >
        <CreateButton label="Create Agreement" />
      </Row>
      <Row
        horizontal="space-between"
        className={classes.lastRow}
      >
        <TasksComponent containerStyles={classes.tasks} />
        {/* <div>{userData.name}</div> */}
      </Row>
    </Column>
  );
}

export default DashboardComponent;
