});

export function CreateShareAgreement() {

  const { id } = useParams();

  // const { corpName, corpPlace, corpDate } = "";
  // const { shareData, personData } = [];

  const classes = useStyles(); 
  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [formData, setFormData] = useState([]);
  const [isLoading, setIsLoading] = useState({
    loading: false
  })

  useEffect(() => {
    getData();
  }, [])

  const getData = async () => {
    setIsLoading(true); 
    const d = await axios
      .get(api, config)
      .then((res) => {
        setFormData(res.data);
        console.log(formData);
        setIsLoading(false); 
      })
      .catch((err) => {
        console.log(err); 
      })
  }

  return (
    <Column>
        <Row>
          <div>Name</div>
          <div horizontal="end">
          <CreateButton label="Download" />
          </div>
        </Row>
      <Row horizontal="space-between" className={classes.lastRow}>
        {!isLoading ? <DisplayData data={formData} /> : <div> Loading... </div>}
      </Row>
    </Column>
  );
}

