import React, {useState, useEffect, useCallback} from 'react';
import { useForm, useController, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import {NiceInput1, NiceSelectInput, NiceNumberInput} from "./Input";
import { Table1 } from "./Table";

function ShareAgreement() {

    const method = useForm() 
    const { register, handleSubmit, setValue, control } = useForm();
    const [shareInfo, setShareInfo] = useState([]); 
    const onSubmit = data => alert(JSON.stringify(data, null, 2));
    
    //const onChange = register('shareinfo'); 
    
    const addShareRow = useCallback(
        () => {
            let l = shareInfo.length + 1;
            const d = [...shareInfo];
                
            const item = {
                refNum:  l,
                shareName: "",
                shareType: "",
                shareTotal: 0
            }
            d.push(item);
            setValue("shareinfo", d); 
            setShareInfo(d);
            
        },[shareInfo]
    );
    
    const removeShareRow = useCallback(
        () => {
            let l = [...shareInfo];
            l.splice(-1, 1);
            setValue("shareinfo", l); 
            setShareInfo(l);
            
        },
        [shareInfo]
    );

    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
        Cell: ({ row: { index } }) => (
          <Input
            type="text"
            name={`shareinfo[${index}].shareName`}
            //control={control}
            {...register(`shareinfo[${index}].shareName`)}
          />
        ),
      },

      {
        Header: "Share Type",
        accessor: "shareType",
        Cell: ({ row: { index } }) => (
          <NiceSelectInput
            name={`shareInfo[${index}][shareType]`}
            control={control}
            options={["Common", "Preferred", "Class A", "Class B", "None"]}
          ></NiceSelectInput>
        ),
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
        Cell: ({ row: { index } }) => (
          <NiceNumberInput
            type="number"
            name={`shareInfo[${index}][shareTotal]`}
            control={control}
          />
        ),
      },
    ];
    return (
      <form onSubmit={handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your company’s Name?</label>
          <input type="text" name="companyName" {...register("companyName")} />
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <Input
            type="date"
            name="companyDate"
            {...register("companyDate")}
          />
        </FormGroup>
        <FormGroup>
          <Label for="table1">Share Table</Label>
          <Table1 columns={shareColumns} data={shareInfo} rowKey={"refNum"} />
          <Button onClick={addShareRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    );
}

export default ShareAgreement;
