import React from "react";
import { createUseStyles, useTheme } from "react-jss";
import { useHistory } from "react-router-dom";
import SLUGS from "../../resources/slugs";
import IconOverview from "../../assets/icon-overview";
import { convertSlugUrl } from "../../resources/utils";
import LogoComponent from "./Logo";
import Menu from "./MenuComponent";
import MenuItem from "./MenuItem";

const useStyles = createUseStyles({
  separator: {
    borderTop: ({ theme }) => `1px solid ${theme.color.lightGrayishBlue}`,
    marginTop: 16,
    marginBottom: 16,
    opacity: 0.06,
  },
});

function SidebarComponent() {
  const { push } = useHistory();
  const theme = useTheme();
  const classes = useStyles({ theme });
  const isMobile = window.innerWidth <= 1080;

  async function logout() {
    push(SLUGS.login);
  }

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  return (
    <Menu isMobile={isMobile}>
      <div style={{ paddingTop: 30, paddingBottom: 30 }}>
        <LogoComponent />
      </div>
      <MenuItem
        id={SLUGS.dashboard}
        title="Dashboard"
        icon={IconOverview}
        onClick={() => onClick(SLUGS.dashboard)}
      />
      <MenuItem
        id={SLUGS.templates}
        title="Templates"
        icon={IconOverview}
        onClick={() => onClick(SLUGS.templates)}
      />
      <MenuItem
        id={SLUGS.lawyers}
        title="Templates"
        icon={IconOverview}
        onClick={() => onClick(SLUGS.lawyers)}
      />
      <MenuItem
        id={SLUGS.supportTickets}
        title="Templates"
        icon={IconOverview}
        onClick={() => onClick(SLUGS.supportTickets)}
      />
      <MenuItem
        id={SLUGS.settings}
        title="Templates"
        icon={IconOverview}
        onClick={() => onClick(SLUGS.settings)}
      />

      <MenuItem
        id="logout"
        title="Logout"
        icon={IconOverview}
        onClick={logout}
      />
    </Menu>
  );
}

export default SidebarComponent;
