import React, {useState, useEffect, useCallback} from 'react';
import { useForm, useController, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import {NiceInput1, NiceSelectInput} from "./Input";
import { Table1 } from "./Table";

function ShareAgreement() {

    const { register, handleSubmit, setValue, control } = useForm();
    const [shareInfo, setShareInfo] = useState([]); 
    const onSubmit = data => alert(JSON.stringify(data, null, 2))

    //const {shareinfo} = useController({name: "shareinfo"})
    
    const addShareRow = useCallback(
        () => {
            let l = shareInfo.length + 1;
            const d = [...shareInfo];
                
            const item = {
                refNum: "#" + l,
                shareName: "",
                shareType: "Common",
                shareTotal: 0
            }
            d.push(item);
            //setValue("shareinfo", d); 
            setShareInfo(d);
            
        },[shareInfo]
    );
    
    function removeShareRow()
    {
        let l = [...shareInfo];
        l.splice(-1, 1);
        //setValue("shareinfo", l);
        setShareInfo(l); 
    }

    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
        // Cell: ({ row: { index } }) => (
        //   <Input
        //     readOnly
        //     type="text"
        //     name={`shareInfo[${index}][refNum]`}
        //     value={`shareInfo[${index}][refNum]`}
        //     {...register(`shareInfo[${index}][refNum]`)}
        //   />
        // ),
      },

      {
        Header: "Share Name",
        accessor: "shareName",
        Cell: ({ row: { index } }) => (
          <NiceInput1
            type="text"
            name={`shareInfo[${index}][shareName]`}
            control={control}
            {...register(`shareInfo[${index}][shareName]`)}
          />
        ),
      },

      {
        Header: "Share Type",
        accessor: "shareType",
        Cell: ({ row: { index } }) => (
          <NiceSelectInput
            name={`shareInfo[${index}][shareType]`}
            control={control}
            options={["Common", "Preferred","ClassA", "ClassB", "None"]}
            {...register(`shareInfo[${index}][shareType]`)}
          >
          </NiceSelectInput>
        ),
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
        Cell: ({ row: { index } }) => (
          <NiceInput1
            type="number"
            name={`shareInfo[${index}][shareTotal]`}
            control={control}
            {...register(`shareInfo[${index}][shareTotal]`)}
          />
        ),
      },
    ];
    return (
      <form onSubmit={handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your companyâ€™s Name?</label>
          <Input type="text" name="companyName" {...register("companyName")} />
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <Input type="date" name="companyDate" {...register("companyDate")} />
        </FormGroup>
        <FormGroup>
          <Label for="table1">Share Table</Label>
          <Table1
            columns={shareColumns}
            data={shareInfo}
            rowKey={"refNum"}
           />
          <Button onClick={addShareRow}>Add Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    );
}

export default ShareAgreement;
