import React from "react";
import { Redirect, Route, Switch } from "react-router-dom";
import SLUGS from "../resources/slugs";
import DashboardComponent from "../pages/Agreements/Dashboard";
import DashTemplate from "./Templates/DashTemplate";
import ShareAgreement from "../pages/Templates/ShareAgreement";
import { CreateShareAgreement } from "../pages/Templates/CreateShareAgreement"
import { NewAgreement } from "../pages/Agreements/NewAgreement"; 

function PrivatePages() {
   const currUser = "http://localhost:8000/api/user";

   const token = localStorage.getItem("token");

   const config = {
     headers: { Authorization: `Bearer ${token}` },
   };
   const [userData, setUserData] = useState([]);
    useEffect(() => {
      getCurrentUser();
    }, []);

    const getCurrentUser = async () => {
      const data = await axios
        .get(currUser, config)
        .then((res) => {
          console.log(res);
          setUserData(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
    };
  return (
    <Switch>
      <Route path={SLUGS.agreements + "/" + userData.id} component={DashboardComponent} />
      <Route path={SLUGS.templates} component={DashTemplate} />
      <Route path={SLUGS.lawyers} render={() => <div>Lawyers</div>} />
      <Route
        path={SLUGS.supportTickets}
        render={() => <div>Support Tickets</div>}
      />
      {/* <Route exact path={SLUGS.settings} component={ProfileSettings} /> */}
      <Route path={SLUGS.shareAgreement} component={ShareAgreement} />
      <Route path={SLUGS.selectTemplate} component={NewAgreement} />
      <Route path={SLUGS.createShare} component={CreateShareAgreement}/>
      <Redirect to={SLUGS.agreements} />
    </Switch>
  );
}

export default PrivatePages;
