import React, { useEffect, useState} from "react";
import { Column, Row } from "simple-flexbox";
import CreateButton from "../../../components/CreateButton";
import { createUseStyles } from "react-jss";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { Paper } from "@material-ui/core"; 
import { useParams } from "react-router";
import SLUGS from "../../../resources/slugs";


const useStyles = createUseStyles({
  
  lastRow: {
    marginTop: 30,
  },

  paperStyle: {
    paddingTop: 60,
    paddingLeft: 30,
    paddingRight: 30,
    paddingBottom: 60, 
    marginLeft: "min(60, 5%)",
    marginRight: 60,
    marginBottom: 50
  },
  container:
  {
      width: 500
  },

  title: {
    marginTop: 10
  },
  paragraph: {
    whiteSpace: "pre-line"
  }
});

export function CreateShareAgreement() {

  const { id } = useParams();
  const { push } = useHistory(); 
  // const { corpName, corpPlace, corpDate } = "";
  // const { shareData, personData } = [];

  const classes = useStyles(); 
  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [formData, setFormData] = useState([]);
  const [isLoading, setIsLoading] = useState({
    loading: false
  })

  useEffect(() => {
    getData();
  }, [])

  const getData = async () => {
    setIsLoading(true); 
    const d = await axios
      .get(api, config)
      .then((res) => {
        setFormData(res.data);
        console.log(res);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        push(SLUGS.agreements)
      })
  }

  return !isLoading ? (
    <div>
      <Row>
        <Column className={[classes.container, classes.title].join(" ")} horizontal="start">
          {formData && formData[0] && <h1>{formData[0].name}</h1>}
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton label="Download" />
        </Column>
      </Row>
      <Row>
        <Row className={classes.lastRow}>
          <DisplayData data={formData} />
        </Row>
      </Row>
    </div>
  ) : (
    <div>Loading...</div>
  );
}

function DisplayData({ data } )
{
  
  const createdDate = data[0].created_at; 
  const classes = useStyles(); 
  const val = JSON.parse(data[0].data);
  
  const corpName = val.companyName;
  const corpPlace = val.companyPlace;
  const corpDate = val.companyDate;
  const shareData = [];
  const personData = [];
  const founderData = [];
  
  
  
  val.shareInfo.map((x, i) => {
    return (shareData[i] = {
      shareID: x.shareID,
      shareName: x.shareName,
      shareClass: x.shareClass,
      shareType: x.shareType,
      shareTotal: x.shareTotal
    });
  });

  val.founderInfo.map((x, i) => {
    return (personData[i] = {
      name: {
        fname: x.fname,
        prefName: x.preferredName
      },
      phone: x.phone,
      email: x.email,
      address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
      IsCompany: [x.role, x.corpName],
      Ownership: x.shareinfo,
      IsBoard: x.onBoard,
      shareinfo: x.shareinfo
    });
  });

  val.founderInfo.map((x, i) => {
    if (x.role === "Founder") {
        founderData.push({
          name: {
            fname: x.fname,
            prefName: x.preferredName
          },
          phone: x.phone,
          email: x.email,
          address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
          IsCompany: [x.role, x.corpName],
          Ownership: x.shareinfo,
          IsBoard: x.onBoard,
          shareinfo: x.shareinfo
        })
    };
  });
  
  const Beginning = function() {
  
    return (
      <div>
        <h6 style={{}}>SHAREHOLDERS AGREEMENT OF {corpName.toUpperCase()}</h6>
        <p>
          THIS AGREEMENT (the “Agreement”), is made as of {corpDate}, between{" "}
          {corpName}, a corporation incorporated under the laws of {corpPlace}{" "}
          (the “Corporation”);
          {personData.map((x) => {
            return `
              ${x.name.fname},
              an individual residing in the City of
              ${x.address[2]},
              ${
                !x.name.prefName
                  ? `${x.address[1]};`
                  : `${x.address[1]} ("${x.name.prefName}");`
              } `;
          })}
          each investor being listed on Schedule “A” annexed hereto, and any
          person who becomes a party hereto by executing an acknowledgement in
          the form annexed hereto as Schedule “B”.
        </p>
      </div>
    );
  }

  const Recitals = function () {
    return (
      // <View style={styles.section}
      <div>
        <h6>Recitals</h6>
        <p>
          {'\t'}WHEREAS the Parties to this Agreement, other than the Corporation,
          together own, directly or indirectly, all of the issued and
          outstanding shares in the capital of the Corporation as of the date of
          this Agreement; AND WHEREAS the Parties to this Agreement wish to
          record their agreement as to the manner in which the Corporation’s
          affairs are to be conducted and to agree upon the terms on which the
          securities of the Corporation, now or hereafter outstanding and held
          by them, will be held, transferred and voted; NOW THEREFORE THIS
          AGREEMENT WITNESSES, that in consideration of the premises and mutual
          covenants and agreements contained in this Agreement, the parties
          covenant and agree as follows:
        </p>
      </div>
      // </View>
    );
  }
  // console.log(beginning())

  const Articles = function () {
    const corpAct = corpPlace === "Canada" ? `CBCA ("Canada Business Corporations Act")` :
      "OBCA (Business Corporations Act of Ontario)";
    const shareTypes = [];
    shareData.map((x , i) => {
      shareTypes[i] = `"${x.shareName}" means the Class ${x.shareClass} ${x.shareType} 
        in the capital of the corporation`
    });

    const founder =
      founderData.length > 1
        ? "Founders means " +
          `${founderData.map((x, i) => {
            return `${
              i == founderData.length - 1
                ? ` and ${x.name.fname}`
                : `${x.name.fname}`
            }`;
          })}` +
        ' \n and "Founder" shall mean any one of them' :
        `\n Founder means ${founderData[0].name.fname}`;
    
    const control = `“Control” means: (a) with respect to any corporation, 
    the ownership, beneficially and legally of voting securities in the 
    capital of such corporation, to which are attached more than fifty(50 %) 
    of the votes that may be cast to elect the directors of such corporation 
    and such votes are sufficient(if exercised) to elect a majority of the directors;
    and(b) with respect to a partnership, trust, syndicate, or other entity,
      actual power or authority to manage and direct the affairs of,
        or ownership of more than fifty(50 %) of the beneficial interest of such entity.`;
    
    return (
      <div>
        <h6>Articles I: Definition</h6>
        <p>
          Whenever and wherever used in this Agreement, the following words and
          terms shall have the following meanings as set out below:
        </p>
        <ol>
          <li>
            “Act” means the {corpAct} as the same may be amended from time to
            time;
          </li>
          <li>“Affiliate” of a Person means another Person where:</li>
          <ol>
            <li>One of them is a Subsidiary of the other, or</li>
            <li>Each of them is controlled by the same Person.</li>
          </ol>
          <li>
            “Agreement” means this Shareholder’s Agreement and all attached
            schedules and all instruments supplemental to or in amendment or
            confirmation of this Agreement;
          </li>
          <li>
            “arm’s length” has the meaning that is has for the purposes of the
            Income Tax Act (Canada);{" "}
          </li>
          <li>
            “Articles” means the articles of incorporation of the Corporation,
            as amended from time to time;
          </li>
          <li>
            “Board” means the board of directors of the Corporation, as
            constituted from time to time in accordance with this Agreement;
          </li>
          <li>
            “Business Day” means any day of the week except Saturday, Sunday, or
            any statutory holiday in the Province of Ontario;
          </li>
          {shareTypes.map((x, i) => {
            return <li key={i}>{x}</li>;
          })}
          <li>
            “Competitor” means any business which is, at the relevant time,
            engaged in the creation, design, development, marketing, sale,
            licensing, or other business activity of any products or services
            that are in form and functionality competitive with any of the
            products or services that are marketed and sold or licensed by the
            Corporation or any product or service that is known by a Shareholder
            to be under development by the Corporation;
          </li>
          <li>{control}</li>
          <li>
            “Family Members” means any parent, spouse, child, spouse of a child,
            grandchild, or sibling of an individual;
          </li>
          <li>{founder}</li>
          <li>
            “Founder Shares” means the Common Shares held by the Founders;
          </li>
          <li>
            “Initial Public Offering” or “IPO” shall mean the Corporation’s
            first underwritten public offering of its Common Shares on any stock
            exchange or market approved in writing by a Preferred Majority;
          </li>
          <li>
            “Investor” means each Person listed in Schedule A of this Agreement
            under the heading “Investors”, so long as such Person holds Class A
            Preferred Shares, together with any other Person that becomes a
            Party to this Agreement as a holder of Class A Preferred Shares
            (including Permitted Transferees of Class A Preferred Shares); and
            “Investor” means all of such Persons collectively;
          </li>
        </ol>
        <h6>1.2 Additional Definitions</h6>
        <p>
          Unless there is an inconsistency in the subject matter or context, or
          unless otherwise provided for in this Agreement, all other words and
          terms used herein that are defined in the Act have the meanings set
          out in that Act.{" "}
        </p>
        <h6>1.1 Rules of Interpretation</h6>
        <p>
          As used in this Agreement, the following rules shall apply to the use
          of the following terms:
        </p>
        <ol>
          <li>
            Consent. Where a provision of this Agreement requires an approval or
            consent and such approval or consent is not delivered within the
            applicable time limit, then unless otherwise specified, the Party
            whose consent or approval is required is conclusively deemed to have
            withheld its approval or consent.
          </li>
          <li>
            Including. Where the word “including” or the word “includes” is used
            in this Agreement, it means “including (or includes) without
            limitation”
          </li>
          <li>
            Number and Gender. Unless the context otherwise necessitates, words
            using the singular include the plural and vice versa, and words
            importing gender shall include all genders.
          </li>
          <li>
            Severability. In the event that one or several of the provisions of
            this Agreement are found to be invalid, illegal, or unenforceable in
            any respect in accordance with any laws or regulations which have
            jurisdiction over said Agreement, the validity, legality, and or
            enforceability of the remaining provisions of this Agreement shall
            not be affected or compromised in any respect. The Parties shall
            strive in good faith to replace such invalid, illegal, or
            unenforceable provisions with effective provisions that accomplish
            to the greatest extent permitted by law and the intentions of the
            Parties, and the economic effect of such effective provisions shall
            be as close as possible to the economic effect of those invalid,
            illegal, or unenforceable provisions.
          </li>
          <li>
            Time Periods. Unless otherwise specified, time periods within or
            following which any payment is to be made or act is to be done will
            be calculated by excluding the day on which the period commences and
            including the day on which the period ends and by extending the
            period to the following Business Day if the last day of the period
            is not a Business Day.
          </li>
          <li>
            Currency. Unless otherwise indicated, all dollar amounts in this
            Agreement shall refer to lawful money of Canada.
          </li>
        </ol>
      </div>
    ); 
  }

  const Article2 = function ()
  {
    return (
      <div>
        <h6>Article 2: Purpose and Scope</h6>
        <h6>2.1 Complinance with Agreement</h6>
        <p>
          The Shareholders shall at all times do all acts and things and vote
          the Issued Shares and otherwise exercise their respective rights as
          shareholders to cause such meetings to be held, resolutions to be
          passed, by-laws to be enacted, documents to be executed and, to the
          extent permitted by Applicable Law, to cause their respective nominees
          on the Board to act, so that at all times the provisions, conditions,
          restrictions and prohibitions contained in this Agreement relating to
          their respective shareholdings in the capital of the Corporation and
          the business and corporate affairs of the Corporation shall fully
          apply, including, without limitation, the amendment or repeal of the
          Articles, by-laws and/or resolutions of the Corporation to the extent
          necessary to resolve any conflict between same and this Agreement so
          that this Agreement shall at all times prevail.{" "}
        </p>
        <h6>2.2 Corporation shall comply </h6>
        <p>
          The Corporation itself undertakes to carry out and become bound by the
          provisions of this Agreement to the fullest extent possible.{" "}
        </p>
        <h6>2.3 Agreement to Vote Shares</h6>
        <p>
          In the event that any action requiring approval pursuant to Section
          X.X of this Agreement is approved by the Board and the required number
          of Investors (a “Special Action”), each Shareholder agrees that it
          shall fully execute and deliver all necessary instruments including
          without limitation deeds, transfers, consents, resolutions,
          share-certificates, or otherwise, that are necessary to complete the
          Special Action at any meeting of shareholders of the Corporation, or
          shall fully execute all written shareholder consents and resolutions
          relating to the Special Action and the completion of the transaction
          contemplated thereunder. Further, each Shareholder hereby expressly
          waives any right to dissent with respect to any such actions which are
          required for the purpose of any Special Action. However, this Section
          2.3 shall only apply if all Shares of a particular class are treated
          in a similar manner.
        </p>
        <h6>2.4 Unanimous Shareholder Agreement</h6>
        <p>
          This Agreement shall be deemed to be a unanimous shareholder agreement
          within the meaning of the Act, and the power of directors to manage or
          oversee the management of the business and affairs of the Corporation
          is restricted according to this Agreement. Furthermore, no amendment
          to this Agreement which affects the rights, powers, and duties of any
          directors is valid or effective until such time that the directors are
          given written notice of the proposed amendment and the opportunity to
          resign.
        </p>
        <h6>ARTICLE 3: FINANCIAL PARTICIPATION IN THE CORPORATION</h6>
        <h6>3.1 Equity</h6>
        <p>
          Each of the Shareholders represents and warrants to each other and to
          the Corporation that:
        </p>
        <ol>
          <li>
            such Shareholder at the date of this Agreement (or if such
            Shareholder becomes a Party to this Agreement after the date of this
            Agreement, then at the time they acquire their Shares), beneficially
            owns, and the same is recorded, the number of Shares set out beside
            the Shareholder’s name on Schedule “A” attached hereto, as
            applicable (as such schedules may be amended from time to time to
            reflect changes in shareholdings);
          </li>
          <li>
            the Shares held by such Shareholder are held beneficially and are
            recorded by such Shareholder, such shares are not subject to any
            mortgage, lien, charge, pledge, encumbrance, security interest, or
            adverse claim, and no Person has any rights to become a holder or
            possessor of any of the Shares or of the certificates representing
            the same, if applicable;
          </li>
          <li>
            if the Shareholder is an individual, then such Shareholder has the
            legal capacity to enter into and execute the terms fully, of this
            Agreement;
          </li>
          <li>
            if the Shareholder is a corporation, that it is duly incorporated
            and validly existing under the laws of its jurisdiction of
            incorporation and that it has the corporate power and capacity to
            own its assets and to enter into and perform its obligations under
            this Agreement;
          </li>
          <li>
            in the case that the Shareholder is a trust, partnership, or joint
            venture, that it is duly constituted under the laws of the
            jurisdiction that governs it and that it has the power and authority
            to own its own assets and to contract into this Agreement;
          </li>
          <li>
            this Agreement has been duly authorized by the Shareholder and duly
            executed and delivered by such Shareholder and that this Agreement
            is binding upon the Shareholder in accordance with the terms herein,
            except for usual exceptions as to bankruptcy and the availability of
            equitable remedies;
          </li>
          <li>
            the execution, delivery and performance of this Agreement does not
            and never will contravene the provisions of its articles, by-laws,
            constating documents or other organizational documents of the
            Corporation or documents to which the Shareholder was created or
            established or the provisions of any agreement to which such
            Shareholder is a party or may be bound to; and,
          </li>
          <li>
            all of the foregoing representations and warranties (excepting those
            in paragraph 1.) shall continue to be true and correct during the
            length of this Agreement.
          </li>
        </ol>
      </div>
    );
    }
  
  const Article5 = function () {
    const directors = [];
    const prefShares = [];
    personData.map((x, i) => {
      if (x.IsBoard) {
        directors.push({
          name: x.name.fname,
          role: x.role
        })
      }
    });
    
    shareData.map((x) => {
      if (x.shareType === "Preferred")
      {
        prefShares.push({
          id: x.shareID,
          name: x.shareName,
          class: x.shareClass, 
        })
        }
    });

  }


  const Article6 = function ()
  {
    const certificate = `“The shares represented by this certificate are subject to
    all the terms and conditions of a Shareholders Agreement made as of ${createdDate}
    as it may be amended from time to time, which agreement contains,
    among other things, restrictions on the right of the holder hereof to transfer or sell the shares.
    A copy of such agreement is on file at the registered office of the Corporation.”`;
    return (
      <div>
        <h6>ARTICLE 6: SHARE RIGHTS</h6>
        <h6>6.1 Restrictions</h6>
        <ol>
          <li>
            Shareholders shall not Transfer any Shares to another Person or
            entity, except as expressly authorized by this Agreement. If Shares
            are transferred in contravention of this Agreement, the Corporation
            shall not Transfer on its ledger such shares or treat the owners of
            the Shares as valid Shareholders or otherwise give such owners any
            of the rights of the Shares that they have been transferred to in
            contravention of this Agreement.
          </li>
          <li>
            The following conditions apply to every Transfer of Shares by a
            Shareholder:
            <ol>
              <li>
                The proposed transferee must be legally bound in writing to
                become a party and be bound by this Agreement (if not already
                legally bound) by signing an acknowledgement substantially in
                the form displayed in Schedule “B”, annexed hereto.
              </li>
              <li>
                Such Transfer must be approved in accordance with the Articles
                and any transfer that follows the requirements of Article 6
                shall be deemed to have been consented to by the Shareholders
                for the purposes of any restrictions on Transfer in the
                Articles; and,
              </li>
              <li>
                Such Shareholder shall provide to the Corporation with written
                assurances, in a form satisfactory to the Corporation, that the
                proposed transfer is exempt from registration and prospectus
                requirements of applicable securities laws, and that all
                appropriate action has been taken to comply with such laws.
              </li>
            </ol>
          </li>
        </ol>
        <h6>6.2 Endorsement on Certificates</h6>
        <p>
          Share certificates of the Corporation shall bear the following
          language in addition to such legends as may be required by applicable
          securities laws:{" "}
        </p>
        <p>{certificate}</p>
        <h6>6.3 Pre-Emptive Rights</h6>
        <p>
          The following provisions shall apply to the allotment and issue by the
          Corporation of any shares in its capital stock (“New Shares”):
        </p>
        <h6>6.4 Permitted Transfers</h6>
        <ol>
          Each Investor may, after giving notice to the Corporation, transfer
          all or any part of its Shares beneficially owned by it to:
          <ol type="a">
            <li>Any Affiliate of such Investor;</li>
            <li>
              Any fund under common management or Control with such Investor (or
              its successor by amalgamation), or whose manager or general
              partner, as applicable, is the same as or an Affiliate of the
              manager or general partner of the Investor (or its successor by
              amalgamation);
            </li>
            <li>
              Any shareholder(s), partner(s), member or investor(s) of such
              Investors where such Transfer is made in connection with a
              distribution of assets to any shareholder(s), partner(s),
              member(s), or investor(s) of the Investor;
            </li>
            <li>
              Any Person in connection with the sale of all or substantially all
              of the assets of such Investor or the liquidation or dissolution
              of such Investor; or
            </li>
            <li>
              Any financial institution that carries on the business of
              providing equity financing as part of a sale of a portfolio of
              equity interests by such Investor;
            </li>
            So long as each transferee agrees to be bound by this Agreement and
            that no transferee or its Affiliates is a Competitor of the
            Corporation.
          </ol>
        </ol>
      </div>
    );
    }
  
  return (
    <div>
      <Paper className={classes.paperStyle}>
        <Beginning />
        <Recitals />
        <Articles />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article2/>
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article6 />
      </Paper>
    </div>
  );
};
