import React, {useContext} from 'react';
import { string } from 'prop-types';
import { Row } from 'simple-flexbox';
import { createUseStyles, useTheme } from 'react-jss';
import SidebarContext from '../Sidebar/SidebarContext';
import DropdownComponent from '../Dropdown/DropdownComponent';
import SLUGS from '../../resources/slugs';
import SidebarComponent from '../Sidebar/Sidebar';

const useStyles = createUseStyles((theme) => ({
  avatar: {
    height: 35,
    width: 35,
    minWidth: 35,
    borderRadius: 50,
    marginLeft: 14,
    border: `1px solid ${theme.color.lightGrayishBlue2}`,
    "@media (max-width: 768px)": {
      marginLeft: 7,
    },
  },
  container: {
    height: 40,
  },
  name: {
    ...theme.typography.itemTitle,
    textAlign: "right",
    "@media (max-width: 768px)": {
      display: "none",
    },
  },
  separator: {
    borderLeft: `1px solid ${theme.color.lightGrayishBlue2}`,
    marginLeft: 32,
    marginRight: 32,
    height: 32,
    width: 2,
    "@media (max-width: 768px)": {
      marginLeft: 14,
      marginRight: 0,
    },
  },
  title: {
    ...theme.typography.title,
    "@media (max-width: 1080px)": {
      marginLeft: 50,
    },
    "@media (max-width: 468px)": {
      fontSize: 20,
    },
  },
  iconStyles: {
    cursor: "pointer",
    marginLeft: 25,
    "@media (max-width: 768px)": {
      marginLeft: 12,
    }
  }

}));

function HeaderComponent()
{
  const currentItem = useContext(SidebarContext);
  const theme = useTheme();
  const classes = useStyles({ theme }); 
  //const { currentItem } = useContext(SidebarComponent);
  let title;
  switch (true) {
    case currentItem === SLUGS.agreements:
      title = "Agreements";
      break;
    case currentItem === SLUGS.templates:
      title = "Templates";
      break;
    case currentItem === SLUGS.lawyers:
      title = "Lawyers";
      break;
    case currentItem === SLUGS.supportTickets:
      title = "Support Tickets";
      break;
    case currentItem === SLUGS.Settings:
      title = "Settings";
      break;
    default:
      title = "";
  }
    
    return (
      <Row
        className={classes.container}
        vertical="center"
        horizontal="space-between"
      >
        <span className={classes.title}>{title}</span>
        <Row vertical="center">
          {/* <div className={css(styles.cursorPointer)}>
            <IconSearch />
          </div>
          <div style={{ marginLeft: 25 }} className={css(styles.cursorPointer)}>
            <IconBellNew />
          </div> */}
          <div className={classes.separator}></div>
          <DropdownComponent
            label={
              <>
                <span className={classes.name}>Jermaine</span>
                <img
                  src='https://avatars3.githubusercontent.com/u/21162888?s=460&v=4'
                  alt='avatar'
                  className={classes.avatar}
                />
              </>
            }
            options={[
              {
                label: 'Settings',
                onClick: () => console.log('settings')
              },

              {
                label: 'Logout',
                onClick: () => console.log('logout')
              }
            ]}
            position={{
              top: 52,
              right: -6
            }}
          />
        </Row>
      </Row>
    );

}

HeaderComponent.propTypes = {
    title: string
};

export default HeaderComponent;
