import React, { useState, useEffect } from "react";
import axios from "axios";
import { Column, Row } from "simple-flexbox";
import { createUseStyles} from "react-jss";
import CreateButton from "../../components/CreateButton";
import Gravatar from "react-gravatar";
import { FormGroup, Input } from "reactstrap";

const useStyles = createUseStyles(({
  container: {
    width: "100%",
    maxWidth: "1300px",
    marginLeft: "auto",
    marginRight: "auto",
  },

  avatar: {
    display: "block",
    overflow: "hidden",
    textOverflow: "ellipsis",
    height: 100,
    width: 100,
    minWidth: 100,
    borderRadius: 50,
    marginRight: 10,
    border: `1px solid rgb(0 0 0 / 15%)`,
    // "@media (max-width: 768px)": {
    //   marginLeft: 7,
    // },
  },

  name: {
    fontSize: 26,
    textAlign: "right",
    "@media (max-width: 768px)": {
      display: "none",
    },
    marginTop: 30,
    marginTop: 30,
    marginRight: 10,
  },

  formControl: {
    border: "1px solid #d5dae2",
    padding: "15px 20px",
    marginBottom: "20px",
    minHeight: "15px",
    fontSize: "13px",
    fontWeight: "normal",
    width: 350,
  },

  buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },

  rowC: {
    display: "flex",
    flexDirection: "row",
  },
  rowLast: {
    marginTop: 50,
  },
}));


function ProfileSettings()
{
  const classes = useStyles();
  const [user, setUser] = useState([])
  const [values, setValues] = useState({})

 
  useEffect(() => {
    getUser();
  }, []);

  const token = sessionStorage.getItem("xrsf");
  const api = "http://localhost:8000/api/user";
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };

  const getUser = async () => {
    await axios
      .get(api, config)
      .then((res) => {
        console.log(res.data);
        setUser(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Here")
  }

  const handleInput = (e) => {
    const { name, value } = e.target;
    setValues({
        ...values,
      [name]:value,
    });
  };

  return (
    <div classes={classes.rowLast}>
      <Row>
        <Column>
          <div className={classes.rowC}>
            <Gravatar
              email={user.email}
              size={100}
              rating="pg"
              default="retro"
              className="CustomAvatar-image"
              style={{ margin: "10px" }}
              protocol="https://"
              className={classes.avatar}
            />
            <span className={classes.name}>{user.name}</span>
          </div>
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton form="profile-form" label="Update Profile" />
        </Column>
      </Row>
      <Row classes={classes.rowLast}>
        <div>
          <form id="profile-form" onSubmit={handleSubmit}>
            <h2>Update Profile</h2>
            <FormGroup>
              
              <label>Full Name</label>
              <input type="hidden"value="something"/>
              <input
                type="text"
                name="name"
                autoComplete="off"
                defaultValue={user.name}
                onChange={handleInput}
              />
              <label>Email</label>
              <input
                type="email"
                name="email"
                autoComplete="off"
                defaultValue={user.email}
                onChange={handleInput}
              />
              <label>Current Password</label>
              <input
                type="password"
                name="oldpassword"
                autoComplete="off"
                onChange={handleInput}
              />
              <label>New Password</label>
              <input
                type="password"
                name="newpassword"
                autoComplete="off"
                onChange={handleInput}
              />
              <label>Repeat Password</label>
              <input
                type="password"
                name="confpassword"
                autoComplete="off"
                onChange={handleInput}
              />
            </FormGroup>
          </form>
        </div>
      </Row>
    </div>
  );
}



export default ProfileSettings;
