import React from 'react';
import { useForm } from "react-hook-form";
import {
    Input,
    FormGroup
} from "reactstrap";
import { Table1 } from "./Table";

function ShareAgreement() {

    const { register, handleSubmit, setValue } = useForm();
    const [shareInfo, setShareInfo] = useState([]); 
    const onSubmit = data => alert(JSON.stringify(data))

    React.useEffect(() => {
        register({ name: "shareInfo" })
    }, [register]);
    
    function addShareRow()
    {
        let l = shareInfo.length
        const d = [
            ...shareInfo,
            {
                refNum: "#" + l,
                shareName: "",
                shareType: "None",
                shareTotal: 0
            }
        ];
        setValue("shareInfo", d);
        setShareInfo(d); 
    };
    
    function removeShareRow()
    {
        let l = [...shareInfo];
        l.splice(-1, 1);
        setValue("shareInfo", l);
        setShareInfo(l); 
    }

    const shareColumns = [
        {
            Header: "Reference Number",
            accessor: "refNum"
        },
        
        {
            Header: "Share Name",
            accessor: "shareName"
        },
        
        {
            Header: "Share Type",
            accessor: "shareType",
            Cell: ({ row: { index } }) => {
                <Input
                  type="select"
                  name={`shareInfo[${index}][shareType]`}
                  innerRef={register}
                >
                  <option>Common</option>
                  <option>Preferred</option>
                  <option>Class A</option>
                  <option>Class B</option>
                  <option>None</option>
                </Input>
            }
        },
        
        {
            Header: "Total Shares",
            accessor: "shareTotal", 
            Cell: ({ row: { index } }) => {
                <Input
                    name={`shareInfo[${index}][shareTotal]`}
                    innerRef={register}
                />
            } 
        }
    ]
    return (
      <form onSubmit={handleSubmit(onSubmit)}>
        <label>
          What is your companyâ€™s Name?
          <div>
            <input type="text" {...register("company_name")} />
          </div>
        </label>
        <div>
          <label>
            When was your company incorporated?
            <div>
              <input
                type="date"
                {...register("company_date", { required: true })}
              />
            </div>
          </label>
        </div>
        <FormGroup>
            <Label for="table1">Share Table</Label>
            <Table1 columns={shareColumns} data={shareInfo}/>
            <Button onClick={addShareRow}>Add Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    );
}

export default ShareAgreement;
