import React, {useState, useEffect} from "react";
import { FormGroup, Input, Label } from "reactstrap";
import { useForm } from "react-hook-form";
import SLUGS from "../../resources/slugs";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { ShareAgreement } from "../Templates/ShareAgreement"; 

export function NewAgreement() {

  const methods = useForm();
  const [step, setStep] = useState(0); 
    
  const checkStep = (data) => {
    if (step == 0) {
      if (data.readTerms === true) {
        localStorage.setItem("data", data)
        setStep(1);
      }
    }
    
    if (step == 1) {
      const formData = localStorage.getItem("data");
      console.log(JSON.stringify(formData, null, 2)); 
    }
  }

    
  const onSubmit = (data) => {
    console.log(JSON.stringify(data, null, 2));
    checkStep(data);
    }

      
  return (
    <div>
      {step === 0 && (
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <h2>Create A New Agreement</h2>
          <FormGroup>
            <label>What is the name of the Agreement</label>
            <Input
              required
              type="text"
              name="agreeName"
              innerRef={methods.register}
            />
          </FormGroup>
          <FormGroup>
            <label>What Template is the Agreement based on</label>
            <Input
              required
              type="select"
              name="tempType"
              innerRef={methods.register}
            >
              <option>Share Agreement</option>
            </Input>
          </FormGroup>
          <FormGroup check>
            <Label check>
              <Input
                type="checkbox"
                id="readTerms"
                name="readTerms"
                innerRef={methods.register}
              />{" "}
              Make sure to read our{" "}
              <a href="https://google.com"> Terms and services </a>
              before continuing
            </Label>
          </FormGroup>
          <input type="submit" />
        </form>
      )}
      {step === 1 && (
        <ShareAgreement
          methods={methods}
        />
      )}
    </div>
  );
    
}
