import React, {useState, useEffect} from "react";
import { FormGroup, Input, Label } from "reactstrap";
import { useForm } from "react-hook-form";
import SLUGS from "../../resources/slugs";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { ShareAgreement } from "../Templates/ShareAgreement"; 

export function NewAgreement() {

  const methods = useForm();
  const { push } = useHistory();
  const [step, setStep] = useState(0);
  const createAgreement = "http://localhost:8000/api/agreements";
  
  const currUser = "http://localhost:8000/api/user";

  const token = localStorage.getItem("token"); 

  const config = {
    headers: {Authorization: `Bearer ${token}`}
  }
  const [userData, setUserData] = useState([]);
  
  useEffect(() => {
    getCurrentUser();
  }, []);

  const getCurrentUser = async () => {
    const data = await axios.get(currUser,
      config)
      .then(res => {
        console.log(res);
        setUserData(res.data); 
      })
      .catch(err => {
        console.log(err); 
    })
  };
    
  const checkStep = async (data) => {
    const d = {
      name: data.agreeName,
      owner: userData.id
    };
    if (step == 0) {
      if (data.readTerms === true) {
        await axios.post(createAgreement, data.agreeName); 
        setStep(1);
      }
    }
    console.log(data) 
  }

    
  const onSubmit = (data) => {
    console.log(JSON.stringify(data, null, 2));
    checkStep(data);
    }

      
  return (
    <div>
      {step === 0 && (
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <h2>Create A New Agreement</h2>
          <FormGroup>
            <label>What is the name of the Agreement</label>
            <Input
              required
              type="text"
              name="agreeName"
              innerRef={methods.register}
            />
          </FormGroup>
          <FormGroup>
            <label>What Template is the Agreement based on</label>
            <Input
              required
              type="select"
              name="tempType"
              innerRef={methods.register}
            >
              <option>Share Agreement</option>
            </Input>
          </FormGroup>
          <FormGroup check>
            <Label check>
              <Input
                type="checkbox"
                id="readTerms"
                name="readTerms"
                innerRef={methods.register}
              />{" "}
              Make sure to read our{" "}
              <a href="https://google.com"> Terms and services </a>
              before continuing
            </Label>
          </FormGroup>
          <input type="submit" />
        </form>
      )}
      {step === 1 && (
        <ShareAgreement
          methods={methods}
        />
      )}
    </div>
  );
    
}
