import React, { useEffect, useState} from "react";
import { Column, Row } from "simple-flexbox";
import CreateButton from "../../../components/CreateButton";
import { createUseStyles } from "react-jss";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { Paper } from "@material-ui/core"; 
import { useParams } from "react-router";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import DeleteIcon from "@material-ui/icons/Delete";
import SLUGS from "../../../resources/slugs";
import { ToWords } from "to-words";


const useStyles = createUseStyles({
  
  lastRow: {
    marginTop: 30,
  },

  paperStyle: {
    background: "white", 
    paddingTop: 60,
    paddingLeft: 30,
    paddingRight: 30,
    paddingBottom: 60, 
    marginLeft: "min(60, 5%)",
    // marginRight: 60,
    marginBottom: 50
  },
  container:
  {
      width: "100%"
  },

  buttonRow: {
    marginTop: 10
  },

  title: {
    marginTop: 10
  },
  paragraph: {
    whiteSpace: "pre-line"
  }
});

export function CreateShareAgreement() {

  const { id } = useParams();
  const { push } = useHistory(); 

  const classes = useStyles(); 
  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [formData, setFormData] = useState([]);
  const [isLoading, setIsLoading] = useState({
    loading: false
  })

  useEffect(() => {
    getData();
  }, [])

  const getData = async () => {
    setIsLoading(true); 
    const d = await axios
      .get(api, config)
      .then((res) => {
        setFormData(res.data);
        console.log(res);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        push(SLUGS.agreements)
      })
  }

  const name = 'Sign & Download'
  return !isLoading ? (
    <div>
      <Row>
        <Column
          className={[classes.container, classes.title].join(" ")}
          horizontal="start"
        >
          {formData && formData[0] && <h1>{formData[0].name}</h1>}
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton label={name} />
        </Column>
      </Row>
      <Row horizontal="end">
        <div>
          <IconButton aria-label="delete">
            <DeleteIcon />
          </IconButton>
        </div>
      </Row>
      <Row>
        <Row className={classes.lastRow}>
          <DisplayData data={formData} />
        </Row>
      </Row>
    </div>
  ) : (
    <div>Loading...</div>
  );
}

function DisplayData({ data } )
{
  
  const createdDate = data[0].created_at; 
  const classes = useStyles(); 
  const val = JSON.parse(data[0].data);
  
  const corpName = val.companyName;
  const corpPlace = val.companyPlace;
  const corpDate = val.companyDate;
  const corpFax = val.companyFax;
  const corpContact = val.companyContact;
  
  const shareData = [];
  const personData = [];
  const founderData = [];
  const directorData = []; 
  
  
  
  val.shareInfo.map((x, i) => {
    return (shareData[i] = {
      shareID: x.shareID,
      shareName: x.shareName,
      shareClass: x.shareClass,
      shareType: x.shareType,
      shareTotal: x.shareTotal
    });
  });

  val.founderInfo.map((x, i) => {
    return (personData[i] = {
      name: {
        fname: x.fname,
        prefName: x.preferredName
      },
      phone: x.phone,
      email: x.email,
      address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
      IsCompany: [x.role, x.corpName],
      Ownership: x.shareinfo,
      IsBoard: x.onBoard,
      shareinfo: x.shareinfo
    });
  });

  val.founderInfo.map((x, i) => {
    if (x.role === "Founder") {
        founderData.push({
          name: {
            fname: x.fname,
            prefName: x.preferredName
          },
          phone: x.phone,
          email: x.email,
          address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
          IsCompany: [x.role, x.corpName],
          Ownership: x.shareinfo,
          IsBoard: x.onBoard,
          shareinfo: x.shareinfo
        })
    };
  });

  val.directInfo.map((x, i) => {
    return (directorData[i] = {
      name: x.name,
      nominee: x.nominee,
      shareinfo: x.shareinfo
    })
  });
  
  const Beginning = function() {
  
    return (
      <div>
        <h6 style={{}}>SHAREHOLDERS AGREEMENT OF {corpName.toUpperCase()}</h6>
        <p>
