  const Article10 = function () {
    return (
      <div>
        <h6>ARTICLE 10: GENERAL TERMS AND PROVISIONS</h6>
        <h6>Application of this Agreement</h6>
        <p>
          The terms of this Agreement shall apply to any Shares that may
          hereafter be issued by the Corporation and to any Shares or other
          Securities:
        </p>
        <ol type="a">
          <li>
            Resulting from the conversion, reclassification, redesignation,
