import React, { useState} from "react";
import { useForm, FormProvider, Controller } from "react-hook-form";
import { Input, FormGroup, Label, Button } from "reactstrap";
import styled from "styled-components";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { SharesTable, FoundersTable} from "./Table";
import "./test.css";
import SLUGS from "../../../resources/slugs";

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      color: palevioletred
      margin: 0;
      padding: 0.8rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }

      Input {
        font-size: 1rem;
        padding: 0;
        margin: 0;
        border: 1;
      }
    }
  }

  .pagination {
    padding: 0.5rem;
  }
`;
export function ShareAgreement() {

  const methods = useForm(); 
  const { push } = useHistory();
  const [shareInfo, setShareInfo] = useState([
    {
      shareID: 1,
      shareName: "",
      shareClass: "Class A",
      shareType: "Common",
      shareTotal: 0,
    },
  ]);
  
  const [founderInfo, setFounderInfo] = useState([
    {
      refNum: 1,
      fname: "",
      preferredName: "",
      role: "Founder",
      streetName: "",
      cityName: "",
      provinceName: "",
      postalCode: "",
      phone: "",
      email: "",
      isCorp: false,
      onBoard: false,
      corpName: "",
      shareinfo: [
        {
          refId: "",
          numHeld: 0,
        },
      ],
    },
  ]);

  const createAgreement = "http://localhost:8000/api/agreements";
 
  const onSubmit = async (data) => {
    
    console.log(JSON.stringify(data, null, 2));
     const token = sessionStorage.getItem("xrsf");
     const config = {
       headers: { Authorization: `Bearer ${token}` },
     };

    const newData = JSON.parse(localStorage.getItem("data"));
    
    const d = {
      name: newData.agreeName,
      data: data,
    };

    localStorage.removeItem("shareData");
    await axios.post(createAgreement, d, config)
      .then(res => {
        console.log(res);
        localStorage.removeItem("shareData");
        push(SLUGS.createShare + res.data.id)
      }
    )

    
  };

  const shareColumns = [
    {
      Header: "Reference Info",
      columns: [
        {
          Header: "Share ID",
          accessor: "refNum",
        },
      ],
    },
    {
      Header: "Share Info",
      columns: [
        {
          Header: "Share Name",
          accessor: "shareName",
        
        },
        {
          Header: "Class",
          accessor: "shareClass",
        },
        {
          Header: "Type",
          accessor: "shareType",
        },
        {
          Header: "Total Shares",
          accessor: "shareTotal",
        },
      ],
    },
    {
      Header: "Actions",
      columns: [
        {
          Header: "Add/Delete Row"
        }
      ]
    }
  ];

  const founderColumns = [

    {
      Header: "Shares Held",
      columns: [
        {
          Header: "Share ID",
          accessor: "refID",
        },

        {
          Header: "Amount Held",
          accessor: "numHeld",
        },
      ],
    },
    {
      Header: "Personal Info",
      columns: [
        {
          Header: "Full Name",
          id: `fname`,
          accessor: "fname",
        },
        {
          Header: "Role",
          accessor: "role",
        },
        {
          Header: "Address",
          accessor: "addressInfo",
        },

        {
          Header: "Phone Number",
          accessor: "phone",
        },

        {
          Header: "Email(Optional)",
          accessor: "email",
        },
      ],
    },
    {
      Header: "Corporation Info",
      columns: [
        {
          Header: "On the Board of Directors",
          accessor: "isBoard",
        },
        {
          Header: "Corporation Name (If Applicable)",
          accessor: "corpName",
        },
      ],
    },
  ];

  return (
    <FormProvider {...methods}>
      <form onSubmit={methods.handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your company’s Name?</label>
          <Input type="text" name="companyName" innerRef={methods.register} />
        </FormGroup>
        <FormGroup>
          <label color="black">What is your company's place of incorporation?</label>
          <Input type="select" name="companyPlace" innerRef={methods.register}>
            <option>Canada</option>
            <option>Ontario</option>
          </Input>
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <Input
            type="date"
            name="companyDate"
            innerRef={methods.register}
          />
        </FormGroup>

        <FormGroup>
          <Label for="sTable">What Does Your Company Have?</Label>
          <div className="Table">
            <Styles>
              <SharesTable
                columns={shareColumns}
                ref={methods.register}
                data={shareInfo}
                setData={setShareInfo}
                methods={methods}
              />
            </Styles>
          </div>
        </FormGroup>
        <FormGroup>
          <Label>Who are the Stakeholders?</Label>
          <div className="Table">
            <Styles>
              <FoundersTable
                columns={founderColumns}
                data={founderInfo}
                ref={methods.register}
                methods={methods}
                setData={setFounderInfo}
                shareData={shareInfo}
              />
            </Styles>
          </div>
          {/* <Button onClick={addFounderRow}>Add Row </Button>{" "}
          <Button onClick={removeFounderRow}>Remove Row</Button> */}
        </FormGroup>
        <input type="submit" />
      </form>
    </FormProvider>
  );
}

export default ShareAgreement;
