import React, {useState, useEffect} from "react";
import { createUseStyles, useTheme } from "react-jss";
import { useHistory } from "react-router-dom";
import axios from "axios";
import SLUGS from "../../resources/slugs";
import { convertSlugUrl } from "../../resources/utils";
import LogoComponent from "./Logo";
import Menu from "./MenuComponent";
import MenuItem from "./MenuItem";
import { GrDocument } from "react-icons/gr";
import { ImInsertTemplate, ImLifebuoy } from "react-icons/im";
import { GiScales } from "react-icons/gi";
import { VscSettingsGear } from "react-icons/vsc"; 

const useStyles = createUseStyles({
  separator: {
    borderTop: ({ theme }) => `1px solid ${theme.color.lightGrayishBlue}`,
    marginTop: 16,
    marginBottom: 16,
    opacity: 0.06,
  },
});

function SidebarComponent() {
  const { push } = useHistory();
  const theme = useTheme();
  const classes = useStyles({ theme });
  const isMobile = window.innerWidth <= 1080;

  const currUser = "http://localhost:8000/api/user";

  const token = localStorage.getItem("token");

  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const [userData, setUserData] = useState([]);

  useEffect(() => {
    getCurrentUser();
  }, []);

  const getCurrentUser = async () => {
    const data = await axios
      .get(currUser, config)
      .then((res) => {
        console.log(res);
        setUserData(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const id = userData.id; 

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  return (
    <Menu isMobile={isMobile}>
      <div style={{ paddingTop: 30, paddingBottom: 30 }}>
        <LogoComponent />
      </div>
      <MenuItem
        id={SLUGS.agreements}
        title="Agreements"
        icon={GrDocument}
        onClick={() => onClick(SLUGS.agreements + id)}
      />
      <MenuItem
        id={SLUGS.templates}
        title="Templates"
        icon={ImInsertTemplate}
        onClick={() => onClick(SLUGS.templates)}
      />
      <MenuItem
        id={SLUGS.lawyers}
        title="Lawyers"
        icon={GiScales}
        onClick={() => onClick(SLUGS.lawyers)}
      />
      <MenuItem
        id={SLUGS.supportTickets}
        title="Support Tickets"
        icon={ImLifebuoy}
        onClick={() => onClick(SLUGS.supportTickets)}
      />
      <MenuItem
        //id={SLUGS.settings}
        items={[SLUGS.profileSettings,
                SLUGS.companySettings,
                SLUGS.subscriptions,
                SLUGS.invoices]}
        title="Settings"
        icon={VscSettingsGear}
      >
        <MenuItem
          id={SLUGS.profileSettings}
          title="Profile Settings"
          icon={ImLifebuoy}
          onClick={() => onClick(SLUGS.profileSettings)}
        />

        <MenuItem
          id={SLUGS.companySettings}
          title="Company Settings"
          icon={ImLifebuoy}
          onClick={() => onClick(SLUGS.companySettings)}
        />

        <MenuItem
          id={SLUGS.subscriptions}
          title="Subscriptions"
          icon={ImLifebuoy}
          onClick={() => onClick(SLUGS.subscriptions)}
        />

        <MenuItem
          id={SLUGS.invoices}
          title="Invoices"
          icon={ImLifebuoy}
          onClick={() => onClick(SLUGS.invoices)}
        />
      </MenuItem>
    </Menu>
  );
}

export default SidebarComponent;
