//     fieldState: { invalid, isTouched, isDirty },
//     formState: { touchedFields, dirtyFields },
//   } = useController({
//     name,
//     control,
//     rules: { required: true },
//     defaultValue: "",
//   });

//   return <Input {...inputProps} />;
// }

export const NiceInput = React.forwardRef(({ type, control, name, innerRef}, ref) =>
{
    const {
      field: { refs, ...inputProps },
      
    } = useController({
      name,
      control,
      rules: { required: true },
      defaultValue: "",
    });

    return (
      <Input
        {...inputProps}
        type={type}
        innerRef={innerRef}
      />
    );

});

export const NiceSelectInput = React.forwardRef(({ control, name, options }, ref) => {
  const {
    field: { refs, ...inputProps },
  } = useController({
    name,
    control,
    rules: { required: true },
    defaultValue: "",
  });

    return (
        <select type="select" {...inputProps} inputref={refs}>
            {options.map(value => (
        <option key={value} value={value}>
          {value}
                </option>
    
            ))}
        </select>
        );

});

export const NiceNumberInput = React.forwardRef(({ control, name }, ref) => {
  const {
    field: { refs, ...inputProps },
  } = useController({
    name,
    control,
    rules: { required: true },
    defaultValue: "",
  });

  return <Input type="number" {...inputProps} inputref={refs} />;
});
