import React from "react";
import { Column, Row } from "simple-flexbox";
import { createUseStyles } from "react-jss";
import TasksComponent from "./AgreementComponent";
import CreateButton from "../../components/CreateButton"; 

const useStyles = createUseStyles({
    buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },
  tasks: {
    marginTop: 0,
    "@media (max-width: 1024px)": {
      marginTop: 30,
    },
  },
  buttonStyle: {
    display: "flex",
    justifyContent: "flex-end",
  },
});



function DashboardComponent() {

  const currUser = "http://127.0.0.1:8000/api/users/current";
  const [userData, setUserData] = useState[{}];

  useEffect(() => {
    getCurrentUser();
  }, []);

  const getCurrentUser = async () => {
    const response = await axios.get(currUser);
    setUserData(response);
  };
  
  const classes = useStyles();
  return (
    <Column>
      <Row
        horizontal="end"
        className={classes.buttonRow}
        breakpoints={{ 1024: "column" }}
      >
        <CreateButton label="Create Agreement" />
      </Row>
      <Row
        horizontal="space-between"
        className={classes.lastRow}
        breakpoints={{ 720: "column" }}
      >
        <TasksComponent containerStyles={classes.tasks} />
        <h1>{userData.name}</h1>
      </Row>
    </Column>
  );
}

export default DashboardComponent;
