import React, { useState} from "react";
import { useForm, FormProvider} from "react-hook-form";
import { Input, FormGroup, Label, Tooltip} from "reactstrap";
import styled from "styled-components";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { SharesTable, FoundersTable} from "./Table";
import "./test.css";
import SLUGS from "../../../resources/slugs";
import { createUseStyles } from "react-jss";
import { BsInfoCircleFill as InfoIcon } from "react-icons/bs";

const Styles = styled.div`
  padding: 1rem;

  table {
    border-spacing: 0;
    border: 1px solid black;

    tr {
      :last-child {
        td {
          border-bottom: 0;
        }
      }
    }

    th,
    td {
      color: palevioletred
      margin: 0;
      padding: 0.8rem;
      border-bottom: 1px solid black;
      border-right: 1px solid black;

      :last-child {
        border-right: 0;
      }

      Input {
        font-size: 1rem;
        padding: 0;
        margin: 0;
        border: 1;
      }
    }
  }

  .pagination {
    padding: 0.5rem;
  }
`;

const useStyles = createUseStyles({
  iconSize: {
    fontSize: 15,
    marginBottom: 5
  }
})
export function ShareAgreement() {

  const methods = useForm();
  const methods1 = useForm();
  const methods2 = useForm();


  const { push } = useHistory();
  const [mergeData, setMergeData] = useState([]); 
  const [step, setStep] = useState(0); 
  const [shareInfo, setShareInfo] = useState([
    {
      shareID: 1,
      shareName: "",
      shareClass: "Class A",
      shareType: "Common",
      shareTotal: 0,
    },
  ]);
  const [shareList, setShareList] = useState([
    {
      id: 1,
      selected: false
    }
  ]);
  
  const [founderInfo, setFounderInfo] = useState([
    {
      refNum: 1,
      fname: "",
      preferredName: "",
      role: "Founder",
      streetName: "",
      cityName: "",
      provinceName: "",
      postalCode: "",
      phone: "",
      email: "",
      isCorp: false,
      onBoard: false,
      corpName: "",
      shareinfo: [
        {
          refId: "",
          numHeld: 0,
        },
      ],
    },
  ]);

  const idata = `shareinfo[${0}]`;
  const [inputList, setInputList] = useState([
    [{ name: idata, index: 0, refID: "1", numHeld: 0 }],
  ]);

  const createAgreement = "http://localhost:8000/api/agreements";
 
  const onSubmit = async (data) => {

    if (step === 0) {
      const item = [...mergeData]
      item.push(data)
      setMergeData(item)
      setStep(1);
    }
    if (step === 1) {
      const item = [...mergeData];
      item.push(data);
      setMergeData(item);
      setStep(2);
    }
    if (step === 2) {
      const fData = {
        ...mergeData[0],
        ...mergeData[1],
        ...data
      }
      console.log(JSON.stringify(fData, null, 2));  
      const token = sessionStorage.getItem("xrsf");
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };

      const newData = JSON.parse(localStorage.getItem("data"));

      const d = {
        name: newData.agreeName,
        data: fData,
      };

      await axios.post(createAgreement, d, config).then((res) => {
        console.log(res);
        localStorage.removeItem("shareData");
        push(SLUGS.createShare + res.data.id);
      });
    }
  };

  const shareColumns = [
    {
      Header: "Reference #",
      id: "shareHeader",
      columns: [
        {
          Header: <HeaderComponent
            title="Share ID"
            info="Share ID for better access to share information" />,
          accessor: "refNum",
          id: "refNum",
        },
      ],
    },
    {
      Header: "Share Info",
      columns: [
        {
          Header: "Share Name",
          accessor: "shareName",
          id: "ShareNum",
        },
        {
          Header: "Class",
          accessor: "shareClass",
        },
        {
          Header: "Type",
          accessor: "shareType",
        },
        {
          Header: "Total Shares",
          accessor: "shareTotal",
        },
      ],
    },
    {
      Header: "Actions",
      columns: [
        {
          Header: "Add/Delete Row",
        },
      ],
    },
  ];

  const founderColumns = [
    {
      Header: "Shares Held",
      columns: [
        {
          Header: "Share ID",
          accessor: "refID",
        },

        {
          Header: "Amount Held",
          accessor: "numHeld",
        },
      ],
    },
    {
      Header: "Personal Info",
      columns: [
        {
          Header: "Full Name",
          id: `fname`,
          accessor: "fname",
        },
        {
          Header: "Role",
          accessor: "role",
        },
        {
          Header: "Address",
          accessor: "addressInfo",
          width: 180,
        },

        {
          Header: "Phone Number",
          accessor: "phone",
        },

        {
          Header: "Email(Optional)",
          accessor: "email",
        },
      ],
    },
    {
      Header: "Corporation Info",
      columns: [
        {
          Header: "On the Board of Directors",
          accessor: "isBoard",
        },
        {
          Header: "Corporation Name (If Applicable)",
          accessor: "corpName",
        },
      ],
    },
    {
      Header: "Actions",
      columns: [
        {
          Header: "Add/Delete Row",
        },
      ],
    },
  ];

return (
  <div>
    {step === 0 && (
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <FormGroup>
            <label>What is your company’s Name?</label>
            <Input
              type="text"
              name="companyName"
              innerRef={methods.register}
            />
            <label color="black">
              What is your company's place of incorporation?
            </label>
            <Input
              type="select"
              name="companyPlace"
              innerRef={methods.register}
            >
              <option>Canada</option>
              <option>Ontario</option>
            </Input>
            <label>When was your company incorporated?</label>
            <Input
              type="date"
              name="companyDate"
              innerRef={methods.register}
            />
            <button type="submit" onClick={methods.handleSubmit(onSubmit)}>
              Next Step
            </button>
          </FormGroup>
        </form>
      </FormProvider>
    )}
    {step === 1 && (
      <FormProvider {...methods1}>
        <form onSubmit={methods1.handleSubmit(onSubmit)}>
          <FormGroup>
            <Label for="sTable">What Does Your Company Have?</Label>
            <div className="Table">
              <Styles>
                <SharesTable
                  columns={shareColumns}
                  ref={methods1.register}
                  data={shareInfo}
                  setData={setShareInfo}
                  methods={methods1}
                  shareList={shareList}
                  setShareList={setShareList}
                />
              </Styles>
            </div>
            <button onClick={() => setStep(0)}>Go Back</button>
            <button type="submit" onClick={methods1.handleSubmit(onSubmit)}>
              Next Step
            </button>
          </FormGroup>
        </form>
      </FormProvider>
    )}
    {step === 2 && (
      <FormProvider {...methods2}>
        <form onSubmit={methods2.handleSubmit(onSubmit)}>
          <FormGroup>
            <Label>Who are the Stakeholders?</Label>
            <div className="Table">
              <Styles>
                <FoundersTable
                  columns={founderColumns}
                  data={founderInfo}
                  ref={methods2.register}
                  methods={methods2}
                  setData={setFounderInfo}
                  shareData={shareInfo}
                  inputList={inputList}
                  setInputList={setInputList}
                />
              </Styles>
            </div>
            <button onClick={() => setStep(1)}>Go Back</button>
            <button type="submit">Finish</button>
          </FormGroup>
        </form>
      </FormProvider>
    )}
  </div>
  );
      
}


export function HeaderComponent({ title, info }) {
  const classes = useStyles();
   const [tooltipOpen, setTooltipOpen] = useState(false);

   const toggle = () => setTooltipOpen(!tooltipOpen);
  return (
    <div>
      <span>{title}{" "}</span>
      <InfoIcon className={classes.iconSize} id="info" />
      <Tooltip placement="right" isOpen={tooltipOpen} target="info" toggle={toggle}>
        {info}
      </Tooltip>
    </div>
  ); 
}

export default ShareAgreement;
