import React, { useState, useEffect } from "react";
import axios from "axios";
import { Column, Row } from "simple-flexbox";
import { createUseStyles, useTheme } from "react-jss";
import CreateButton from "../../components/CreateButton";
import Gravatar from "react-gravatar";
//import '../../assets/login.css'

const useStyles = createUseStyles((theme) => ({
  container: {
    width: "100%",
    maxWidth: "1300px",
    marginLeft: "auto",
    marginRight: "auto",
  },

  avatar: {
    display: "block",
    overflow: "hidden",
    textOverflow: "ellipsis",
    height: 100,
    width: 100,
    minWidth: 100,
    borderRadius: 50,
    marginRight: 10,
    border: `1px solid rgb(0 0 0 / 15%)`,
    // "@media (max-width: 768px)": {
    //   marginLeft: 7,
    // },
  },

  name: {
    fontSize: 26,
    textAlign: "right",
    "@media (max-width: 768px)": {
      display: "none",
    },
    marginTop: 30,
    marginTop: 30,
    marginRight: 10,
  },

  formControl: {
    border: "1px solid #d5dae2",
    padding: "15px 20px",
    marginBottom: "20px",
    minHeight: "15px",
    fontSize: "13px",
    fontWeight: "normal",
    width: 350,
  },

  buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },

  rowC: {
    display: "flex",
    flexDirection: "row",
  },
  rowLast: {
    display: "flex",
    flexDirection: "row",
    marginTop: 30,
  },
}));


function ProfileSettings()
{
  const theme = useTheme(); 
  const classes = useStyles(theme);
  const [user, setUser] = useState([]);

   const token = sessionStorage.getItem("xrsf");
   const api = "http://localhost:8000/api/user";
   const config = {
     headers: { Authorization: `Bearer ${token}` },
   };

   useEffect(() => {
     getUser();
   }, []);

   const getUser = async () => {
     const data = await axios
       .get(api, config)
       .then((res) => {
         console.log(res.data);
         setUser(res.data);
       })
       .catch((err) => {
         console.log(err);
       });
   };


  return (
    <div>
      <Row>
        <Column>
          <div className={classes.rowC}>
            <Gravatar
              email={user.email}
              size={100}
              rating="pg"
              default="retro"
              className="CustomAvatar-image"
              style={{ margin: "10px" }}
              protocol="https://"
              className={classes.avatar}
            />
            <span className={classes.name}>{user.name}</span>
          </div>
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton label="Update Profile" />
        </Column>
      </Row>
      <Row>
        <main className="d-flex align-items-center min-vh-100 py-3 py-md-0">
          <div className="container">
            <div className="card login-card">
              <div className="row no-gutters">
                <div className="col-md-12">
                  <div className="card-body">
                    <img
                      src={login_pic}
                      alt="login"
                      className="login-card-img"
                    />
                    <p className="login-card-description">Sign In</p>
                    <form onSubmit={handleSubmit}>
                      <div className="form-group">
                        {response.error && <p>{response.message}</p>}
                        <label htmlFor="email" className="sr-only">
                          Email
                        </label>
                        <input
                          type="email"
                          name="email"
                          id="email"
                          className="form-control"
                          placeholder="Email address"
                          value={fields.email}
                          onChange={handleLoginChange}
                        />
                        {errors.email && <p>{errors.email}</p>}
                      </div>
                      <div className="form-group mb-4">
                        <label htmlFor="password" className="sr-only">
                          Password
                        </label>
                        <input
                          type="password"
                          name="password"
                          id="password"
                          className="form-control"
                          placeholder="Password"
                          value={fields.password}
                          onChange={handleLoginChange}
                        />
                        {errors.password && <p>{errors.password}</p>}
                      </div>
                      <button
                        name="submit"
                        className="btn btn-block login-btn mb-4"
                        type="submit"
                      >
                        Login
                      </button>
                    </form>
                    <nav className="login-card-footer-nav">
                      <Link to={SLUGS.register}>Register</Link> |{" "}
                      <Link to={SLUGS.forgotPassword}>Forgot Password?</Link>
                    </nav>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </main>
      </Row>
    </div>
  );
}



export default ProfileSettings;
