import React, {useState, useEffect, useCallback} from 'react';
import { useForm, FormProvider, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import { SharesTable, FoundersTable } from "./Table";
 
let list = [];

function ShareAgreement() {
    const [shareInfo, setShareInfo] = useState([]);
    const [founderInfo, setFounderInfo] = useState([]);
    const [corpInfo, setCorpInfo] = useState([]);
    const methods = useForm();
    const onSubmit = (data) => {
        console.log(JSON.stringify(data, null, 2));
    }
    
    React.useEffect(() => {
        methods.register({ name: "shareInfo" })
    }, [methods])
    

    const addShareRow = function () {
        let l = shareInfo.length + 1;
        const d = [...shareInfo];
        
            const item = {
            refNum: l,
            shareName: "",
            shareClass: "",
            shareType: "",
            shareTotal: 0,
        };
        list.push(item.refNum);
        d.push(item);
        setShareInfo(d);
        
        
    }

    const removeShareRow = function(){
        let l = [...shareInfo];
        l.pop();
        
        if (list[0] != null) {
            list.pop();
        }
        setShareInfo(l);
            
    };

    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
      },

      {
        Header: "Share Type",
        columns: [
          {
            Header: "Class",
            accessor: "shareClass",

          },
          {
            Header: "Type",
            accessor: "shareType",
          },
        ],
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
      },
    ];

    const addFounderRow = function () {
        let l = founderInfo.length + 1;
        const d = [...founderInfo];

        const item = {
        fname: "",
        preferredName: "",
        refId: "",
        numHeld: 0,
        addressInfo: "", 
        phone: "",
        email: "",
        };
        d.push(item); 
        setFounderInfo(d);
    };

    const founderColumns = [
      {
        Header: "Full Name",
        accessor: "fname",

      },

        {
            Header: "Shares Held",
            columns: [
                {
                    Header: "Reference #",
                    accessor: "refID",
                },

                {
                    Header: "# Held",
                    accessor: "numHeld",

                },
            ],
        },
      {
        Header: "Address",
        accessor: "addressInfo"
      },

      {
        Header: "Phone Number",
        accessor: "phone",
      },

      {
        Header: "Email(Optional)",
        accessor: "email",
      },
    ];

    return (
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <FormGroup>
            <label>What is your companyâ€™s Name?</label>
            <input type="text" name="companyName" ref={methods.register} />
          </FormGroup>
          <FormGroup>
            <label>When was your company incorporated?</label>
            <input
              type="date"
              name="companyDate"
              ref={methods.register}
              //method={methods.register}
            />
          </FormGroup>
          <FormGroup>
            <Label for="sTable">What Does Your Company Have?</Label>
            <Controller
              name="sTable"
              control={methods.control}
              defaultValue="name"
              as={
                <SharesTable
                  name="sTable"
                  columns={shareColumns}
                  ref={methods.register}
                  data={shareInfo}
                  methods={methods}
                />
              }
            />
            <Button onClick={addShareRow}>Add Row </Button>{" "}
            <Button onClick={removeShareRow}>Remove Row</Button>
          </FormGroup>
          <FormGroup>
            <Label>Who are the founders?</Label>
            <FoundersTable
              columns={founderColumns}
              data={founderInfo}
              ref={methods.register}
              methods={methods}
            />
            <Button onClick={addFounderRow}>Add Row </Button>{" "}
            {/* <Button onClick={removeShareRow}>Remove Row</Button> */}
          </FormGroup>
          <input type="submit" />
        </form>
      </FormProvider>
    );
}

export default ShareAgreement;
