export const NiceInput = React.forwardRef(({ type, control, name, innerRef}, ref) =>
{
    const {
      field: { refs, ...inputProps },
      
    } = useController({
      name,
      control,
      rules: { required: true },
      defaultValue: "",
    });

    return (
      <Input
        {...inputProps}
        type={type}
        innerRef={innerRef}
      />
    );

});
