import React, {ComponentProps} from "react";
import { Input } from "reactstrap";
import { InputType } from "reactstrap";
import { useController, useForm } from "react-hook-form";

// export default function NiceInput({ control, name}) {
//   const {
//     field: { ref, ...inputProps },
//     fieldState: { invalid, isTouched, isDirty },
//     formState: { touchedFields, dirtyFields },
//   } = useController({
//     name,
//     control,
//     rules: { required: true },
//     defaultValue: "",
//   });

//   return <Input {...inputProps} />;
// }

export const NiceInput = React.forwardRef(({ type, control, name, innerRef}) =>
{
    const {
      //     field: { ref, ...inputProps },
      //     fieldState: { invalid, isTouched, isDirty },
      //     formState: { touchedFields, dirtyFields },
    } = useController({
      name,
      control,
      rules: { required: true },
      defaultValue: "",
    });

    return (
      <Input
        {...inputProps}
        type={type}
        ref={refs}
        innerRef={innerRef}
      />
    );

});

export const SelectNiceInput = React.forwardRef(({ control, name, options }) => {
  const {
    field: { refs, ...inputProps },
    meta: { invalid, isTouched, isDirty },
  } = useController({
    name,
    control,
    rules: { required: true },
    defaultValue: "",
  });

  return (<Input
    {...inputProps}
    type="select"
    ref={refs}>
    {options.map(value => (
      <options key={value} value={value}>
        {value}
      </options>
    ))}
  </Input>
  );
});
