import React, {useState} from "react";
import { FormGroup, Input, Label } from "reactstrap";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

import SLUGS from "../../resources/slugs";

export function NewAgreement() {

  const methods = useForm();
  const { push } = useHistory(); 
    
  // const checkStep = (data) => {
  //   if (step === 0) {
  //     if (data.readTerms === true) {
  //       localStorage.setItem("data", JSON.stringify(data))
  //       setStep(1);
  //     }
  //   }
    
  //   if (step === 1) {
  //     const formData = localStorage.getItem("data");
  //     console.log(JSON.stringify(formData, null, 2));
  //     push(SLUGS.shareAgreement) 
  //   }
  // }

    
  const onSubmit = (data) => {
    console.log(JSON.stringify(data, null, 2));
    localStorage.setItem("data", JSON.stringify(data));
    push(SLUGS.shareAgreement) 
    }

      
  return (
    <div>
      <form onSubmit={methods.handleSubmit(onSubmit)}>
        <h2>Create A New Agreement</h2>
        <FormGroup>
          <label>What is the name of the Agreement</label>
          <Input
            required
            type="text"
            name="agreeName"
            innerRef={methods.register}
          />
        </FormGroup>
        <FormGroup>
          <label>What Template is the Agreement based on</label>
          <Input
            required
            type="select"
            name="tempType"
            innerRef={methods.register}
          >
            <option>Share Agreement</option>
          </Input>
        </FormGroup>
        <FormGroup check>
          <Label check>
            <Input
              type="checkbox"
              id="readTerms"
              name="readTerms"
              innerRef={methods.register}
            />{" "}
            Make sure to read our{" "}
            <a href="https://google.com"> Terms and services </a>
            before continuing
          </Label>
        </FormGroup>
        <button type="submit">Confirm</button>
      </form>
    </div>
      );
}
