import React from 'react';
import { string } from 'prop-types';
import { Row } from 'simple-flexbox';
import { cryeateUseStyles, useTheme } from 'react-jss';
import DropdownComponent from '../Dropdown/DropdownComponent';
import { createUseStyles } from 'react-jss';

const useStyles = createUseStyles((theme) => ({
  avatar: {
    height: 35,
    width: 35,
    borderRadius: 50,
    marginLeft: 14,
    border: "1px solid #DFE0EB",
  },
  container: {
    height: 40,
  },
  cursorPointer: {
    cursor: "pointer",
  },
  name: {
    fontFamily: "Muli",
    fontStyle: "normal",
    fontWeight: 600,
    fontSize: 14,
    lineHeight: "20px",
    textAlign: "right",
    letterSpacing: 0.2,
  },
  separator: {
    borderLeft: "1px solid #DFE0EB",
    marginLeft: 32,
    marginRight: 32,
    height: 32,
    width: 2,
  },
  title: {
    fontFamily: "Muli",
    fontStyle: "normal",
    fontWeight: "bold",
    fontSize: 24,
    lineHeight: "30px",
    letterSpacing: 0.3,
  },

  iconStyles: {
    cursor: "pointer",
    marginLeft: 25,
    "@media (max-width: 768px)": {
      marginLeft: 12,
    }
  }
}));

function HeaderComponent(props)
{
  const { icon, title, ...otherProps } = props;
  const { currentItem } = useContext(SidebarComponent);
  const theme = useTheme();
  const classes = useStyles({ theme }); 
  //const { currentItem } = useContext(SidebarComponent);
    
    return (
      <Row
        className={classes.container}
        vertical="center"
        horizontal="space-between"
        {...otherProps}
      >
        <span className={classes.title}>{title}</span>
        <Row vertical="center">
          {/* <div className={css(styles.cursorPointer)}>
            <IconSearch />
          </div>
          <div style={{ marginLeft: 25 }} className={css(styles.cursorPointer)}>
            <IconBellNew />
          </div> */}
          <div className={classes.seperator}></div>
          <DropdownComponent
            label={
              <>
                <span className={classes.name}>Jermaine Cole</span>
                <img
                  src='https://avatars3.githubusercontent.com/u/21162888?s=460&v=4'
                  alt='avatar'
                  className={css(styles.avatar)}
                />
              </>
            }
            options={[
              {
                label: 'Settings',
                onClick: () => console.log('settings')
              },

              {
                label: 'Logout',
                onClick: () => console.log('logout')
              }
            ]}
            position={{
              top: 52,
              right: -6
            }}
          />
        </Row>
      </Row>
    );

}

HeaderComponent.propTypes = {
    title: string
};

export default HeaderComponent;
