import React, { useEffect, useState } from "react";
import { Column, Row } from "simple-flexbox";
import CreateButton from "../../../components/CreateButton";
import { createUseStyles } from "react-jss";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { Paper } from "@material-ui/core";
import { useParams } from "react-router";
import { CircularProgress } from "@material-ui/core";
import SLUGS from "../../../resources/slugs";
import {
  Beginning,
  Recitals,
  Articles,
  Article2,
  Article4,
  Article6,
  Article7,
  Article8,
  Article10,
  Article10b,
  Article9,
} from "./SharePages";
import { DeleteDialog, EditDialog } from "./Info";
import { PDFExport, savePDF } from "@progress/kendo-react-pdf"; 

const useStyles = createUseStyles({
  lastRow: {
    marginTop: 10,
  },

  paperStyle: {
    background: "white",
    padding: 40,
    marginBottom: 50,
    marginTop: 20,
    boxShadow: "unset !important",
    border: `1px solid #d8d8d8`,
  },
  container: {
    width: "100%",
    maxWidth: "1300px",
    marginLeft: "auto",
    marginRight: "auto",
  },

  buttonRow: {
    marginTop: 10,
  },

  title: {
    marginTop: 10,
    color: "#000",
    fontSize: "30px",
  },
  paragraph: {
    whiteSpace: "pre-line",
  },

  rowC: {
    display: "flex",
    flexDirection: "row",
  },

  // rowBtn: {
  //   marginRight: "10px !important",
  // },
});

export function CreateShareAgreement() {
  const { id } = useParams();
  const { push } = useHistory();

  const classes = useStyles();
  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  const api = `http://localhost:8000/api/agreements/${id}`;

  const [formData, setFormData] = useState([]);
  const [isLoading, setIsLoading] = useState({
    loading: false,
  });

  useEffect(() => {
    getData();
  }, []);

  const getData = async () => {
    setIsLoading(true);
    await axios
      .get(api, config)
      .then((res) => {
        setFormData(res.data);
        console.log(res);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        push(SLUGS.agreements);
      });
  };

  const name = "Sign & Download";
  return !isLoading ? (
    <div>
      <Row>
        <Column
          className={[classes.container, classes.title].join(" ")}
          horizontal="start"
        >
          {formData && formData[0] && <h1>{formData[0].name}</h1>}
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <div className={classes.rowC}>
            <EditDialog
              api={api}
              config={config}
              agreeName={formData[0].name}
              getData={getData}
            />
            <DeleteDialog config={config} api={api} />
            <CreateButton label={name} />
          </div>

          {/* <Show data={formData}/> */}
        </Column>
      </Row>
      <Row>
        <Row className={classes.lastRow}>
          <DisplayData data={formData} />
        </Row>
      </Row>
    </div>
  ) : (
    <div>
      <CircularProgress color="black" />
    </div>
  );
}

function DisplayData({ data }) {
  const createdDate = data[0].created_at;
  const classes = useStyles();
  const val = JSON.parse(data[0].data);

  const corpName = val.companyName;
  const corpPlace = val.companyPlace;
  const corpDate = val.companyDate;
  const corpFax = val.companyFax;
  const corpContact = val.companyContact;
  const optOut = val.optOut;
  const control = val.control;

  const shareData = [];
  const personData = [];
  const founderData = [];
  const directorData = [];

  val.shareInfo.map((x, i) => {
    return (shareData[i] = {
      shareID: x.shareID,
      shareName: x.shareName,
      shareClass: x.shareClass,
      shareType: x.shareType,
      shareTotal: x.shareTotal,
    });
  });

  val.founderInfo.map((x, i) => {
    return (personData[i] = {
      ID: x.personID,
      name: {
        fname: x.fname,
        prefName: x.preferredName,
      },
      phone: x.phone,
      email: x.email,
      address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
      IsCompany: [x.role, x.corpName],
      IsBoard: x.onBoard,
      shareinfo: x.shareinfo,
    });
  });

  val.founderInfo.map((x, i) => {
    if (x.role === "Founder") {
      founderData.push({
        ID: x.personID,
        name: {
          fname: x.fname,
          prefName: x.preferredName,
        },
        phone: x.phone,
        email: x.email,
        address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
        IsCompany: [x.role, x.corpName],
        IsBoard: x.onBoard,
        shareinfo: x.shareinfo,
      });
    }
  });
  if (!optOut) {
    val.directInfo.map((x, i) => {
      return (directorData[i] = {
        ID: x.personID,
        name: x.name,
        nominee: x.nominee,
        shareinfo: x.shareinfo,
      });
    });
  }

  return (
    <div>
      <Paper className={classes.paperStyle}>
        <Beginning
          corpName={corpName}
          corpData={corpDate}
          corpPlace={corpPlace}
          personData={personData}
        />
        <Recitals />
        <Articles
          corpPlace={corpPlace}
          shareData={shareData}
          founderData={founderData}
          majControl={control}
        />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article2 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article4 directorData={directorData} shareData={shareData} optOut={optOut} />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article6 createdDate={createdDate} />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article7 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article8 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article9 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article10 />
      </Paper>
      <Paper className={classes.paperStyle}>
        <Article10b
          corpName={corpName}
          corpContact={corpContact}
          corpFax={corpFax}
        />
      </Paper>
    </div>
  );
}

const ShareDoc = ({ data }) => {
  const createdDate = data[0].created_at;
  const classes = useStyles();
  const val = JSON.parse(data[0].data);

  const corpName = val.companyName;
  const corpPlace = val.companyPlace;
  const corpDate = val.companyDate;
  const corpFax = val.companyFax;
  const corpContact = val.companyContact;

  const shareData = [];
  const personData = [];
  const founderData = [];
  const directorData = [];

  val.shareInfo.map((x, i) => {
    return (shareData[i] = {
      shareID: x.shareID,
      shareName: x.shareName,
      shareClass: x.shareClass,
      shareType: x.shareType,
      shareTotal: x.shareTotal,
    });
  });

  val.founderInfo.map((x, i) => {
    return (personData[i] = {
      name: {
        fname: x.fname,
        prefName: x.preferredName,
      },
      phone: x.phone,
      email: x.email,
      address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
      IsCompany: [x.role, x.corpName],
      Ownership: x.shareinfo,
      IsBoard: x.onBoard,
      shareinfo: x.shareinfo,
    });
  });

  val.founderInfo.map((x, i) => {
    if (x.role === "Founder") {
      founderData.push({
        name: {
          fname: x.fname,
          prefName: x.preferredName,
        },
        phone: x.phone,
        email: x.email,
        address: [x.streetName, x.provinceName, x.cityName, x.postalCode],
        IsCompany: [x.role, x.corpName],
        Ownership: x.shareinfo,
        IsBoard: x.onBoard,
        shareinfo: x.shareinfo,
      });
    }
  });

  val.directInfo.map((x, i) => {
    return (directorData[i] = {
      name: x.name,
      nominee: x.nominee,
      shareinfo: x.shareinfo,
    });
  });

  return (
    <div>
      <Beginning
        corpName={corpName}
        corpData={corpDate}
        corpPlace={corpPlace}
        personData={personData}
      />
      <Recitals />
      <Articles
        corpPlace={corpPlace}
        shareData={shareData}
        founderData={founderData}
        classes={classes}
      />
    </div>
  );
};

