import React, { useState }from "react";
import { useTable } from "react-table";
import { Input, Label, FormGroup } from "reactstrap";

export const SharesTable = React.forwardRef(({ columns, data, methods}, ref) => {
  // Use the state and functions returned from useTable to build your UI
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data,
    });

  // Render the UI for your table
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>
                  <Input
                    readOnly
                    type="text"
                    name={`shareInfo[${i}][refNum]`}
                    value={i+1}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="text"
                    name={`shareInfo[${i}][shareName]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareClass]`}
                    innerRef={methods.register}
                  >
                    <option>Class A</option>
                    <option>Class B</option>
                    <option>None</option>
                  </Input>
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareType]`}
                    innerRef={methods.register}
                  >
                    <option>Common</option>
                    <option>Preferred</option>
                  </Input>
                </td>

                <td>
                  <Input
                    type="number"
                    name={`shareInfo[${i}][shareTotal]`}
                    innerRef={methods.register}
                  />
                </td>
              </tr>
            );
        })}
      </tbody>
    </table>
  );
});

export const FoundersTable = React.forwardRef(
  ({ columns, data, methods, addRow}, ref) => {
    // Use the state and functions returned from useTable to build your UI
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
      useTable({
        columns,
        data,
      });

    // Render the UI for your table
    const [checked, setChecked] = useState(true);
    const handleCheckChange = () => {
      
      if (checked)
      {
        //value = "false"
        console.log("false");
      }
      else {
        //value = "true"
        console.log("true");
      }
      setChecked(!checked);
      //console.log(value);
    };
    
    return (
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][fname]`}
                    placeholder="Full Name"
                    innerRef={methods.register}
                  />
                  <Input
                    type="text"
                    name={`founderInfo[${i}][preferredName]`}
                    placeholder="Preferred Name"
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="select"
                    name={`founderInfo[${i}][role]`}
                    innerRef={methods.register}
                  >
                    <option>Founder</option>
                    <option>Investor</option>
                  </Input>
                  <Label check>Corporation?</Label>
                  <input
                    type="checkbox"
                    name={`founderInfo[${i}][isCorp]`}
                    onChange={handleCheckChange}
                    defaultChecked={checked}
                    //value="false"
                    ref={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][refID]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="number"
                    name={`founderInfo[${i}][numHeld]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="text"
