import React, { useState }from "react";
import { useTable } from "react-table";
import { Input, Label, Button } from "reactstrap";
import { SelectNiceInput } from "./Input";

export const SharesTable = React.forwardRef(({ columns, data, methods}, ref) => {
  // Use the state and functions returned from useTable to build your UI
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data,
    });

  // Render the UI for your table
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>
                  <Input
                    readOnly
                    type="text"
                    name={`shareInfo[${i}][refNum]`}
                    value={i+1}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="text"
                    name={`shareInfo[${i}][shareName]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareClass]`}
                    innerRef={methods.register}
                  >
                    <option>Class A</option>
                    <option>Class B</option>
                    <option>None</option>
                  </Input>
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareType]`}
                    innerRef={methods.register}
                  >
                    <option>Common</option>
                    <option>Preferred</option>
                  </Input>
                </td>

                <td>
                  <Input
                    type="number"
                    name={`shareInfo[${i}][shareTotal]`}
                    innerRef={methods.register}
                  />
                </td>
              </tr>
            );
        })}
      </tbody>
    </table>
  );
});

let L = []; 
export const FoundersTable = React.forwardRef(
  ({ columns, data, methods, addRow}, ref) => {
    // Use the state and functions returned from useTable to build your UI
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
      useTable({
        columns,
        data,
      });

    // Render the UI for your table
    // const [person, setPerson] = useState({
    //   refNum: ""

    // });
    
    const handleChange = (e) => {
      
      const { name, value } = e.target
      const num = parseInt(name[12])
      const index = L.indexOf(num)
      if (value === "Investor")
      {
        if (!L.includes(num))
        {
           L.push(num);
          }
       
      }
      else {
        L.splice(index, 1)
      }
      console.log(L)
    };
    
    return (
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>
                  <Input
                    readOnly
                    type="text"
                    name={`founderInfo[${i}][refNum]`}
                    value={i + 1}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][fname]`}
                    placeholder="Full Name"
                    innerRef={methods.register}
                  />
