import React, { useState } from 'react';
import { arrayOf, element, func, number, oneOfType, shape, string } from 'prop-types';
import { Column } from 'simple-flexbox';
import { StyleSheet, css } from "aphrodite";
import IconArrowUp from '../../assets/icon-arrow-up'; 

const useStyles = StyleSheet.create(
    {
        arrowContainer: {
            position: 'absolute',
            top: -19,
            right: 15
        },
        dropdownButton: {
            alignItems: 'center',
            background: 'transparent',
            border: 'none',
            cursor: 'pointer',
            display: 'flex',
            padding: 0,
            outline: 'none'
        },
        dropdownContainer: {
            position: 'relative'
        },
        dropdownItemsContainer: {
            background: 'white',
            border: `1px solid ${theme.color.lightGrayishBlue2}`,
            borderRadius: 5,
            minWidth: 170,
            padding: 0,
            position: 'absolute',
            width: '100%',
            top: ({ position }) => position.top,
            right: ({ position }) => position.right,
            bottom: ({ position }) => position.bottom,
            left: ({ position }) => position.left,
            '& button:first-of-type:hover div > svg > path': {
                fill: theme.color.paleBlue
            }
        },
        dropdownItem: {
            cursor: 'pointer',
            background: 'transparent',
            border: 'none',
            fontSize: 16,
            outline: 'none',
            padding: '10px 10px',
            '&:hover': {
                background: theme.color.paleBlue
            },
            '&:after': {
                content: '" "',
                display: 'block',
                position: 'relative',
                bottom: -10,
                width: '100%',
                height: 1,
                background: theme.color.paleBlue
            },
            '&:last-child:after': {
                content: '',
                display: 'none'
            }
        }
    });
    
function DropdownComponent({ label, options, position })
{
    const [useOpenMenu, setOpenMenu] = useState(false);

    function onDropdownClick()
    {
        setOpenMenu((prev) => !prev)
    }

    function onItemClick(onClick)
    {
        setOpenMenu(false);
        onClick && onClick();
    }

    return (
        <Column className={css(styles.dropdownContainer)}>
            <button classname={css(styles.dropdownButton)}>
                {label}
            </button>
            {useOpenMenu && (
                <Column classname={css(styles.dropdownItemsContainer)}>
                    {options.map((option, index) => (
                        <button
                            key={'option-${index}'}
                            className={css(styles.dropdownItem)}
                            onClick={() => onItemClick(option.onClick)}>
                        
                            {option.label}
                            {index === 0 && (
                                <div className={css(styles.arrowContainer)}>
                                    <IconArrowUp />
                                </div>
                            )}
                        </button>
                    ))}
                </Column>
            )}
        </Column>
    )
}

DropdownComponent.propTypes = {
    label: oneOfType([string, element]),
    options: arrayOf(
        shape({
            label: oneOfType([string, arrayOf(element)]),
            onClick: func
        })
    ),
    position: shape({
        top: number,
        right: number,
        bottom: number,
        left: number
    })
};

DropdownComponent.defaultProps = {
    position: {
        top: 52,
        right: -6
    }
};

export default DropdownComponent;
