import React, {useState, useEffect} from "react"; 
import axios from "axios"; 
import {useHistory} from "react-router-dom"; 
import { createUseStyles } from "react-jss";
import CreateButton from "../../components/CreateButton";
import SLUGS from "../../resources/slugs";
import TicketComponent from "./TicketComponent";
import {Row, Column} from "simple-flexbox"; 


const useStyles = createUseStyles({
    buttonRow: {
      "@media (max-width: 768px)": {
        marginTop: 0,
      },
    },
    lastRow: {
      marginTop: 30,
    },
    tasks: {
      flexGrow: 1,
      marginTop: 0,
      "@media (max-width: 1024px)": {
        marginTop: 30,
      },
    },
    buttonStyle: {
      display: "flex",
      justifyContent: "flex-end",
    },
  });

export default function TicketDashboard() 
{
    const {push} = useHistory();
    const [data, setData] = useState();
    const [loading, setLoading] = useState(); 
    const classes = useStyles(); 

    useEffect(() => {
        getData(); 
    }, []); 

    const token = sessionStorage.getItem("xrsf");
    const config = {
      headers: { 'Authorization': `Bearer ${token}` },
    };

    const api = `${process.env.REACT_APP_API}/api/tickets`

    const getData = async () =>{
        setLoading(true);
        await axios
        .get(api, config)
        .then((res) => {
            console.log(res.data)
            setData(res.data); 
            setLoading(false);
        }) 
        .catch((err) => {
            setLoading(false); 
        }) 
    }

    return(
        <div>
        <Row horizontal="end" className={classes.buttonRow}>
            <CreateButton
            label="New Support Ticket"
            //onClick={() => onClick(SLUGS.selectTemplate)}
            />
        </Row>
        <Column className={classes.lastRow}> 
            <TicketComponent
                data={data}
                loading={loading}
            />
        </Column>
        </div>
    ); 

}
