import React, { useState, useEffect }from "react";
import { useTable } from "react-table";
import { Controller } from "react-hook-form";
import { Input, Label, Button, Row, Col } from "reactstrap";
import "./test.css"; 

export const SharesTable = React.forwardRef(({ columns, data, methods}, ref) => {
  // Use the state and functions returned from useTable to build your UI
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data,
    });

  // Render the UI for your table
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>{i + 1}</td>
                <td>
                  <Input
                    type="text"
                    name={`shareInfo[${i}][shareName]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareClass]`}
                    innerRef={methods.register}
                  >
                    <option>Class A</option>
                    <option>Class B</option>
                    <option>None</option>
                  </Input>
                </td>
                <td>
                  <Input
                    type="select"
                    name={`shareInfo[${i}][shareType]`}
                    innerRef={methods.register}
                  >
                    <option>Common</option>
                    <option>Preferred</option>
                  </Input>
                </td>

                <td>
                  <Input
                    type="number"
                    name={`shareInfo[${i}][shareTotal]`}
                    innerRef={methods.register}
                  />
                </td>
              </tr>
            );
        })}
      </tbody>
    </table>
  );
});

export const FoundersTable = React.forwardRef(
  ({ columns, data, methods, inputList, setInputList }, ref) => {
    // Use the state and functions returned from useTable to build your UI
    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
      useTable({
        columns,
        data,
        
      });
    
    const handleInput = (e, i) => {
      const { name, value } = e.target;
      const list = [...inputList];
      list[i][name] = value;
      console.log(rows.index);
      setInputList(list);
    };
    
    const handleAddClick = (i) => {
      const list = [...inputList];
      const lastIndex = inputList[i].length - 1;
      console.log(inputList)
      list[i].push({
        name: `shareinfo[${list[i][lastIndex].index + 1}]`,
        index: list[i][lastIndex].index + 1,
      });
      setInputList(list);
    };

    const handleRemoveClick = (i) => {
      const pos = [...inputList];
      pos[i].pop();
      setInputList(pos);
    };
    return (
      <table {...getTableProps()}>
        <thead>
          {headerGroups.map((headerGroup) => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column) => (
                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody {...getTableBodyProps()}>
          {rows.map((row, i) => {
            prepareRow(row);
            return (
              <tr {...row.getRowProps()}>
                <td>
                  <Controller
                    name={`founderInfo[${i}][refNum]`}
                    control={methods.control}
                    defaultValue={i+1}
                    as={<p>
                      {i + 1}
                    </p>}
                  />
                </td>
                <td colSpan="2">
                  {inputList[i].map((x, index) => {
                    return (
                      <div key={index}>
                        <Row>
                          <Col>
                            <Input
                              name={`founderInfo[${i}][${x.name}][refID]`}
                              placeholder="Enter First Name"
                              //value={x.refID}
                              onChange={(e) => handleInput(e, index)}
                              innerRef={methods.register}
                            />
                          </Col>
                          <Col>
                            <Input
                              name={`founderInfo[${i}][${x.name}][numHeld]`}
                              placeholder="No. Shares held"
                              type="number"
                              onChange={(e) => handleInput(e, index)}
                              innerRef={methods.register}
                            />
                          </Col>
                        </Row>
                      </div>
                    );
                  })}
                  <div className="btn-box">
                    <button onClick={() => handleAddClick(i)}>
                      Add
                    </button>
                    {inputList[i].length !== 1 && (
                      <button
                        className="mr10"
                        onClick={() => handleRemoveClick(i)}
                      >
                        Remove
                      </button>
                    )}
                  </div>
                </td>
                <td>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][fname]`}
                    placeholder="Full Name"
                    innerRef={methods.register}
                  />
                  <Input
                    type="text"
                    name={`founderInfo[${i}][preferredName]`}
                    placeholder="Preferred Name"
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="select"
                    name={`founderInfo[${i}][role]`}
                    innerRef={methods.register}
                  >
                    <option>Founder</option>
                    <option>Investor</option>
                    <option>Corporation</option>
                  </Input>
                </td>
                <td>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][streetName]`}
                    placeholder="Street Name"
                    innerRef={methods.register}
                  />
                  <span>
                    <Input
                      type="text"
                      name={`founderInfo[${i}][cityName]`}
                      placeholder="City"
                      innerRef={methods.register}
                    />

                    <Input
                      type="select"
                      name={`founderInfo[${i}][provinceName]`}
                      placeholder="Province"
                      innerRef={methods.register}
                    >
                      <option value="AB">Alberta</option>
                      <option value="BC">British Columbia</option>
                      <option value="MB">Manitoba</option>
                      <option value="NB">New Brunswick</option>
                      <option value="NL">Newfoundland and Labrador</option>
                      <option value="NS">Nova Scotia</option>
                      <option value="ON">Ontario</option>
                      <option value="PE">Prince Edward Island</option>
                      <option value="QC">Quebec</option>
                      <option value="SK">Saskatchewan</option>
                      <option value="NT">Northwest Territories</option>
                      <option value="NU">Nunavut</option>
                      <option value="YT">Yukon</option>
                    </Input>
                  </span>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][postalCode]`}
                    placeholder="Postal Code"
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="tel"
                    pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
                    name={`founderInfo[${i}][phone]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="email"
                    name={`founderInfo[${i}][email]`}
                    innerRef={methods.register}
                  />
                </td>
                <td>
                  <input
                    type="checkbox"
                    name={`founderInfo[${i}][onBoard]`}
                    ref={methods.register}
                  />
                </td>
                <td>
                  <Input
                    type="text"
                    name={`founderInfo[${i}][corpName]`}
                    placeholder="Enter Corp Name"
                    innerRef={methods.register}
                  />
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    );
  }
);
