import React, {useState, useEffect, useCallback} from 'react';
import { useForm, FormProvider, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import { SharesTable, FoundersTable } from "./Table";
 
let list = [];

function ShareAgreement() {
    const [shareInfo, setShareInfo] = useState([]);
    const [founderInfo, setFounderInfo] = useState([]);
    const [corpInfo, setCorpInfo] = useState([]);
    const methods = useForm();
    const onSubmit = (data) => {
        console.log(JSON.stringify(data, null, 2));
    }
    
    React.useEffect(() => {
        methods.register({ name: "shareInfo" })
    }, [methods])
    

    const addShareRow = function () {
        let l = shareInfo.length + 1;
        const d = [...shareInfo];
        


        const item = {
            refNum: l,
            shareName: "",
            shareClass: "",
            shareType: "",
            shareTotal: 0,
        };
        list.push(item.refNum);
        d.push(item);
        setShareInfo(d);
        
        
    }

    const removeShareRow = function(){
        let l = [...shareInfo];
        l.pop();
        
        if (list[0] != null) {
            list.pop();
        }
        setShareInfo(l);
            
    };
    const overrideValue = (index, override) => {
        let name = [...shareInfo]
        name[index].shareName = override;
        setShareInfo({ name }); 
    }
    
    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
      },

      {
        Header: "Share Type",
        columns: [
          {
            Header: "Class",
            accessor: "shareClass",

          },
          {
            Header: "Type",
            accessor: "shareType",
          },
        ],
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
      },
    ];

    const addFounderRow = function () {
        let l = founderInfo.length + 1;
        const d = [...founderInfo];

        const item = {
        fname: "",
        preferredName: "",
        refId: "",
        numHeld: 0,
        streetName: "",
        cityName: "",
        provinceName: "",
        postalCode: "",
        phone: "",
        email: "",
        };
        d.push(item); 
        setFounderInfo(d);
    };

    const founderColumns = [
      {
        Header: "Full Name",
        accessor: "fname",
        Cell: ({ row: { index } }) => (
          <Input
            type="text"
            name={`founderinfo[${index}][fname]`}
            // innerRef={register}
          />
        ),
      },

      {
        Header: "Shares Held",
        columns: [
          {
            Header: "Reference #",
            accessor: "refID",
          },

          {
            Header: "# Held",
            accessor: "numHeld",
            Cell: ({ row: { index } }) => (
              <Input
                type="number"
                name={`founderinfo[${index}][numHeld]`}
                // innerRef={register}
              />
            ),
          },
        ],
      },

      {
        Header: "Address",
        columns: [
          {
            Header: "Street Name",
            accessor: "streetName",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][streetName]`}
                // innerRef={register}
              />
            ),
          },
          {
            Header: "City",
            accessor: "cityName",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][cityName]`}
                // innerRef={register}
              />
            ),
          },
          {
            Header: "Province",
            accessor: "provinceName",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`founderinfo[${index}][provinceName]`}
                // innerRef={register}
              >
                <option value="AB">Alberta</option>
                <option value="BC">British Columbia</option>
                <option value="MB">Manitoba</option>
                <option value="NB">New Brunswick</option>
                <option value="NL">Newfoundland and Labrador</option>
                <option value="NS">Nova Scotia</option>
                <option value="ON">Ontario</option>
                <option value="PE">Prince Edward Island</option>
                <option value="QC">Quebec</option>
                <option value="SK">Saskatchewan</option>
                <option value="NT">Northwest Territories</option>
                <option value="NU">Nunavut</option>
                <option value="YT">Yukon</option>
              </Input>
            ),
          },
          {
            Header: "Postal Code",
            accessor: "postalCode",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][postalCode]`}
                // innerRef={register}
              />
            ),
          },
        ],
      },

      {
        Header: "Phone Number",
        accessor: "phone",
        Cell: ({ row: { index } }) => (
          <Input
            type="tel"
            pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
            name={`founderinfo[${index}][phone]`}
            // innerRef={register}
          />
        ),
      },

      {
        Header: "Email(Optional)",
        accessor: "email",
        Cell: ({ row: { index } }) => (
          <Input
            type="email"
            name={`founderinfo[${index}][email]`}
            // innerRef={register}
          />
        ),
      },
    ];

    return (
      <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit(onSubmit)}>
          <FormGroup>
            <label>What is your company’s Name?</label>
            <input type="text" name="companyName" ref={methods.register} />
          </FormGroup>
          <FormGroup>
            <label>When was your company incorporated?</label>
            <input
              type="date"
              name="companyDate"
              ref={methods.register}
              //method={methods.register}
            />
          </FormGroup>
          <FormGroup>
            <Label for="sTable">What Does Your Company Have?</Label>
            <Controller
              name="sTable"
              control={methods.control}
              defaultValue="name"
              as={
                <SharesTable
                  name="sTable"
                  columns={shareColumns}
                  ref={methods.register}
                  data={shareInfo}
                  methods={methods}
                />
              }
            />
            <Button onClick={addShareRow}>Add Row </Button>{" "}
            <Button onClick={removeShareRow}>Remove Row</Button>
          </FormGroup>
          <FormGroup>
            <Label for="table2">Who are the founders?</Label>
            <FoundersTable
              columns={founderColumns}
              data={founderInfo}
              ref={methods.register}
              methods={methods}
            />
            <Button onClick={addFounderRow}>Add Row </Button>{" "}
            {/* <Button onClick={removeShareRow}>Remove Row</Button> */}
          </FormGroup>
          <input type="submit" />
        </form>
      </FormProvider>
    );
}

export default ShareAgreement;
