const useStyles = createUseStyles({
  iconSize: {
    fontSize: 15,
    marginBottom: 3
  },

  spacing: {
    marginTop: 5,
    marginBottom: 2,
    marginRight: 5,
  }
});

function ShareAgreement() 
{

  const methods = useForm();
  const methods1 = useForm();
  const methods2 = useForm();
  const methods3 = useForm(); 

  const { push } = useHistory();
  const [mergeData, setMergeData] = useState([]);
  const [directData, setDirectData] = useState([]);
  const [step, setStep] = useState(0); 
  const [shareInfo, setShareInfo] = useState([
    {
      shareID: 1,
      shareName: "",
      shareClass: "Class A",
      shareType: "Common",
      shareTotal: 0,
    },
  ]);
  let optOut = false; 
  const [shareList, setShareList] = useState([
    {
      id: 1,
      selected: false
    }
  ]);
  
  const [founderInfo, setFounderInfo] = useState([
    {
      refNum: 1,
      fname: "",
      preferredName: "",
      role: "Founder",
      streetName: "",
      cityName: "",
      provinceName: "",
      postalCode: "",
      phone: "",
      email: "",
      isCorp: false,
      onBoard: false,
      corpName: "",
      shareinfo: [
        {
          refId: "",
          numHeld: 0,
        },
      ],
    },
  ]);

  const idata = `shareinfo[${0}]`;
  const [inputList, setInputList] = useState([
    [{ name: idata, index: 0, refID: "1", numHeld: 0 }],
  ]);

  const createAgreement = "http://localhost:8000/api/agreements";
 
  const onSubmit = async (data) => {

    let i = [];
    setDirectData(i);
    
    if (step === 0) {
      const item = [...mergeData]
      item[0] = data;
      setMergeData(item)
      setStep(1);
    }
    if (step === 1) {
      const item = [...mergeData];
      item[1] = data;
      setMergeData(item);
      setStep(2);
    }
    if (step === 2) {
      console.log(data);
      const shares = mergeData[1].shareInfo;
      const persons = data.founderInfo;
      let errors = []

      

      for (let i = 0; i < shares.length; i++) {
        let shareTotal = 0
        for (let j = 0; j < persons.length; j++) {
          if (persons[j].shareinfo[i]) {
            var num = parseInt(persons[j].shareinfo[i].numHeld);
            shareTotal += num;
          }
        }
        if (shares[i].shareTotal < shareTotal) {
          console.log("PANIC")
          errors.push(shareTotal)
        }
      }
      
      if (errors.length === 0) {
          const item = [...mergeData];
          item[2] = data;
          setMergeData(item);
        
        
        const d = [...directData]; 
        data.founderInfo.map((x) => {
          if (x.onBoard) {
            d.push({
              name: x.fname,
              shareinfo: x.shareinfo,
              nominee: "Founder",
              quorum: 0
            })
          } 
        })
        setDirectData(d); 
        setStep(4); 
        
      }
    }
    if (step === 4) {
      
      if (optOut === false) {
        console.log(directData)
        const direct = {
          directInfo: []
        }

        directData.map((x, i) => {
          direct.directInfo[i] = {
            name: x.name,
            nominee: x.nominee,
            shareinfo: x.shareinfo,
            quorum: data.quorum
          };
        })

        const finalData = {
          ...mergeData[0],
          ...mergeData[1],
          ...mergeData[2],
          ...direct
        }
        console.log(JSON.stringify(finalData, null, 2));
        sendData(finalData);
      }
    }
  }

  const sendData = async (data) => 
  {
    console.log(JSON.stringify(data, null, 2));
    const token = sessionStorage.getItem("xrsf");
    const config = {
      headers: { Authorization: `Bearer ${token}` },
    };

    const newData = JSON.parse(localStorage.getItem("data"));

    const d = {
      name: newData.agreeName,
      data: data,
    };

    await axios.post(createAgreement, d, config).then((res) => {
      console.log(res);
      localStorage.removeItem("shareData");
      push(SLUGS.createShare + res.data.id);
    });
        
  }
  const shareColumns = [
    {
      Header: "Reference #",
      id: "shareHeader",
      columns: [
        {
          Header: (
            <HeaderComponent
              title="Share ID"
              info="Share ID for better access to share information"
              id="shareID"
            />
          ),
          accessor: "refNum",
          id: "refNum",
        },
      ],
    },
    {
      Header: "Share Info",
      columns: [
        {
          Header: (
            <HeaderComponent
              title="Share Name"
              info="What is the name of the share?"
              id="share2"
            />
          ),
          accessor: "shareName",
          id: "ShareNum",
        },
        {
          Header: (
            <HeaderComponent
              title="Share Class"
              info="What is the class of the share?"
              id="share3"
            />
          ),
          accessor: "shareClass",
          id: "ShareClass",
        },
        {
          Header: (
            <HeaderComponent
              title="Share Type"
              info="What type fo share is the currently being referred to"
              id="share4"
            />
          ),
          accessor: "shareType",
          id: "ShareType",
        },
        {
          Header: (
            <HeaderComponent
              title="Share Total"
              info="What are the total amount of shares this company currently has?"
              id="share5"
            />
          ),
          accessor: "shareTotal",
          id: "ShareTotal",
        },
      ],
    },
    {
      Header: "Actions",
      columns: [
        {
          Header: <HeaderComponent
            title="Add/Delete"
            info="Add or delete this row"
            id="actions"/>,
          id: "Actions",
        },
      ],
    },
  ];

  const founderColumns = [
    {
      Header: "Shares Held",
      columns: [
        {
          Header: "Share ID",
          accessor: "refID",
        },

        {
          Header: "Amount Held",
          accessor: "numHeld",
        },
      ],
    },
    {
      Header: "Personal Info",
      columns: [
        {
          Header: "Full Name",
          id: `fname`,
          accessor: "fname",
        },
        {
          Header: "Role",
          accessor: "role",
        },
        {
          Header: "Address",
          accessor: "addressInfo",
          width: 180,
        },

        {
          Header: "Phone Number",
          accessor: "phone",
        },

        {
          Header: "Email(Optional)",
          accessor: "email",
        },
      ],
    },
    {
      Header: "Corporation Info",
      columns: [
        {
          Header: "On the Board of Directors",
          accessor: "isBoard",
        }
      ],
    },
    {
      Header: "Actions",
      columns: [
        {
          Header: <HeaderComponent
            title="Add/Delete"
            info="Add or delete this row"
            id="actions" />,
          accessor: "acts"
        },
      ],
    },
  ];

  return (
    <div>
      {step === 0 && (
        <FormProvider {...methods}>
          <form onSubmit={methods.handleSubmit(onSubmit)}>
            <FormGroup>
              <HeaderComponent
                title="What is your company's Name?"
                info="Write Down Legal Company name, or Company you represent"
                id="compName"
              />
            </FormGroup>
            <Input type="text" name="companyName" innerRef={methods.register} />
            <FormGroup>
              <HeaderComponent
                title="What is your company's place of incorporation?"
                info="Is you Company located in Greater Canada or Ontario"
                id="compPlace"
              />
              <Input
                type="select"
                name="companyPlace"
                innerRef={methods.register}
              >
                <option>Canada</option>
                <option>Ontario</option>
              </Input>
            </FormGroup>
            <FormGroup>
              <HeaderComponent
                title="When was your company incorporated?"
                info="When was your company founded?"
                id="compDate"
              />
              <Input
                type="date"
                name="companyDate"
                innerRef={methods.register}
              />
              <button type="submit" onClick={methods.handleSubmit(onSubmit)}>
                Next Step
              </button>
            </FormGroup>
          </form>
        </FormProvider>
      )}
      {step === 1 && (
        <FormProvider {...methods1}>
          <form onSubmit={methods1.handleSubmit(onSubmit)}>
            <FormGroup>
              <Label for="sTable">What Does Your Company Have?</Label>
              <div className="Table">
                <Styles>
                  <SharesTable
                    columns={shareColumns}
                    ref={methods1.register}
                    data={shareInfo}
                    setData={setShareInfo}
                    methods={methods1}
                    shareList={shareList}
                    setShareList={setShareList}
                  />
                </Styles>
              </div>
              <button onClick={() => setStep(0)}>Go Back</button>
              <button type="submit" onClick={methods1.handleSubmit(onSubmit)}>
                Next Step
              </button>
            </FormGroup>
          </form>
        </FormProvider>
      )}
      {step === 2 && (
        <FormProvider {...methods2}>
          <form onSubmit={methods2.handleSubmit(onSubmit)}>
            <FormGroup>
              <Label>Who are the Stakeholders?</Label>
              <div className="Table">
                <Styles>
                  <FoundersTable
                    columns={founderColumns}
                    data={founderInfo}
                    ref={methods2.register}
                    methods={methods2}
                    setData={setFounderInfo}
                    shareData={shareInfo}
                    inputList={inputList}
                    setInputList={setInputList}
                  />
                </Styles>
              </div>
              <button onClick={() => setStep(1)}>Go Back</button>
              <button type="submit" onClick={methods2.handleSubmit(onSubmit)}>
                Next Step
              </button>
            </FormGroup>
          </form>
        </FormProvider>
      )}
      {step === 4 && (
        <FormProvider {...methods3}>
          <form onSubmit={methods3.handleSubmit(onSubmit)}>
            <Styles>
              <DirectorsQuest
                data={directData}
                setData={setDirectData}
                optOut={optOut}
                methods={methods3}
              />
              <h6>Q2: Quorum rules</h6>
              <label>
                What is the minimum number of Directors required to reach
                quorum?
              </label>

              <Input
                required
                type="text"
                name={`quorum`}
                innerRef={methods.register}
              />
            </Styles>
            <button onClick={() => setStep(2)}>Go Back</button>
            <button type="submit">Finish</button>
          </form>
        </FormProvider>
      )}
    </div>
  );
      
}
export default ShareAgreement;

export function HeaderComponent({ title, info, id }) {
  const classes = useStyles();
   const [tooltipOpen, setTooltipOpen] = useState(false);

   const toggle = () => setTooltipOpen(!tooltipOpen);
  return (
    <div>
      <label className={classes.spacing}>{title}{"  "}</label>
      <InfoIcon className={classes.iconSize} id={id} />
      <Tooltip placement="top" isOpen={tooltipOpen} target={id} toggle={toggle}>
        {info}
      </Tooltip>
    </div>
  ); 
}

const DirectorsQuest = React.forwardRef(
  ({ data, optOut, methods, setData }, ref) => {

  const directData = [...data];
  
  const handleDirectChange = (i, e) => {
    const { id, value } = e.target;
    const list = [...data];
    list[i][id] = value;
    setData(list);
    
  }
  return (
    directData.length > 0 ? (
      directData.map((x, i) => {
        return (
          <div>
            <label>
              Director {i + 1} is {x.name} nominated by the
            </label>
            <Input
              required
              id="nominee"
              name={`directorInfo[${i}][nominee]`}
              type="select"
              onChange={(e) => handleDirectChange(i, e)}
              value={data[i].nominee}
              innerRef={methods.register}
            >
              <option>Founder</option>
              <option>Common</option>
              <option>Preferred</option>
            </Input>
          </div>
        );
      })
      
    ) : (
      <div>
        <label>
          You have not selected any directors, Do you wish to opt out
        </label>
        {!optOut && (
          <button required onClick={() => (optOut = true)}>
            Opt out
          </button>
        )}
      </div>
    )
  );
}
);
