import React, { useState, useEffect } from "react";
import axios from "axios";
import { Column, Row } from "simple-flexbox";
import { createUseStyles} from "react-jss";
import CreateButton from "../../components/CreateButton";
import Gravatar from "react-gravatar";
import { FormGroup, Input } from "reactstrap";

const useStyles = createUseStyles(({
  container: {
    width: "100%",
    maxWidth: "1300px",
    marginLeft: "auto",
    marginRight: "auto",
  },

  avatar: {
    display: "block",
    overflow: "hidden",
    textOverflow: "ellipsis",
    height: 100,
    width: 100,
    minWidth: 100,
    borderRadius: 50,
    marginRight: 10,
    border: `1px solid rgb(0 0 0 / 15%)`,
    // "@media (max-width: 768px)": {
    //   marginLeft: 7,
    // },
  },

  name: {
    fontSize: 26,
    textAlign: "right",
    "@media (max-width: 768px)": {
      display: "none",
    },
    marginTop: 30,
    marginTop: 30,
    marginRight: 10,
  },

  formControl: {
    border: "1px solid #d5dae2",
    padding: "15px 20px",
    marginBottom: "20px",
    minHeight: "15px",
    fontSize: "13px",
    fontWeight: "normal",
    width: 350,
  },

  buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },

  rowC: {
    display: "flex",
    flexDirection: "row",
  },
  rowLast: {
    marginTop: 50,
  },
}));


function ProfileSettings()
{
  const classes = useStyles();
  const [user, setUser] = useState([])
  const [values, setValues] = useState({ })
  const [isLoading, setIsLoading] = useState(false); 
  const [response, setResponse] = useState({
    error: false,
    message: ""
  })
 
  useEffect(() => {
    getUser();
  }, []);

  const token = sessionStorage.getItem("xrsf");
  const api = "http://localhost:8000/api/user";
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };

  const getUser = async () => {
    setIsLoading(true);
    await axios
      .get(api, config)
      .then((res) => {
        console.log(res.data);
        const response = {
          errors: false,
          message: "Profile Successfully Updated"
        }
        setUser(res.data);
        setResponse(response);
        setIsLoading(false);
      })
      .catch((err) => {
        console.log(err);
        setIsLoading(false);
      });
  };


  const putApi = `http://localhost:8000/api/users/${user.id}`; 
  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios
      .put(putApi, values)
      .then((res) => {
        console.log(res.data)
      })
      .catch((err) => {
        const error = err.response.data;
        if (error.type === "password") {
          console.log(error.errors)
          if (error.errors.conf_password) {
            console.log("Hello")
            setResponse({
              error: true,
              message: error.errors.conf_password,
            });
          }
          if (error.errors.new_password) {
            console.log("Hello1");
            setResponse({
              error: true,
              message: error.errors.new_password,
            });
          }
          if (error.errors.password) {
            console.log("Hello2");
            setResponse({
              error: true,
              message: error.errors.password,
            });
          }
          
        }
        if (error.type === "email") {
          setResponse({
            error: true,
            message: error.errors,
          }); 
        }
        else {
          setResponse({
            error: true,
            message: error.errors
          }); 
        }
        console.log(error)
      })
  }

  const handleInput = (e) => {
    const { name, value } = e.target;
    setValues({
        ...values,
      [name]:value,
    });
  };

  return ( !isLoading ?
    <div classes={classes.rowLast}>
      <Row>
        <Column>
          <div className={classes.rowC}>
            <Gravatar
              email={user.email}
              size={100}
              rating="pg"
              default="retro"
              className="CustomAvatar-image"
              style={{ margin: "10px" }}
              protocol="https://"
              className={classes.avatar}
            />
            <span className={classes.name}>{user.name}</span>
          </div>
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton form="profile-form" label="Update Profile" />
        </Column>
      </Row>
      <Row classes={classes.rowLast}>
        <Input type="hidden" value="prayer"/>
        <div>
          <form id="profile-form" onSubmit={handleSubmit}>
            <h2>Update Profile</h2>
            {/* {response.error && <p>{response.message}</p>} */}
            <FormGroup>
              <label>Full Name</label>
              <input type="hidden" value="something" />
              <Input
                type="text"
                name="name"
                autoComplete="off"
                defaultValue={user.name}
                onChange={handleInput}
              />
              <label>Email</label>
              <Input
                type="email"
                name="email"
                autoComplete="user-email"
                defaultValue={user.email}
                onChange={handleInput}
              />
              <label>Current Password</label>
              <Input
                required
                type="password"
                name="password"
                autoComplete="old-password"
                onChange={handleInput}
              />
              <label>New Password</label>
              <Input
                type="password"
                name="new_password"
                autoComplete="new-password"
                onChange={handleInput}
              />
              <label>Repeat Password</label>
              <Input
                type="password"
                name="conf_password"
                autoComplete="conf-password"
                onChange={handleInput}
              />
            </FormGroup>
          </form>
        </div>
      </Row>
    </div>
    :
    <div>
      Loading...
    </div>
  );
}



export default ProfileSettings;
