import React from "react";
import { Column, Row } from "simple-flexbox";
import { createUseStyles, useTheme } from "react-jss";
import CreateButton from "../../components/CreateButton";
//import '../../assets/login.css'

const useStyles = createUseStyles((theme) => ({
  container: {
    width: "100%",
    maxWidth: "1300px",
    marginLeft: "auto",
    marginRight: "auto",
  },

  avatar: {
    display: "block",
    overflow: "hidden",
    textOverflow: "ellipsis",
    height: 35,
    width: 35,
    minWidth: 35,
    borderRadius: 50,
    marginRight: 10,
    border: `1px solid rgb(0 0 0 / 15%)`,
    "@media (max-width: 768px)": {
      marginLeft: 7,
    },
  },

  formControl: {
    border: "1px solid #d5dae2",
    padding: "15px 20px",
    marginBottom: "20px",
    minHeight: "15px",
    fontSize: "13px",
    fontWeight: "normal",
    width: 350,
  },

  buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },
}));


function ProfileSettings()
{
  const theme = useTheme(); 
  const classes = useStyles(theme);
  
  return (
    <div>
      <Row>
        <Column>
          <Gravatar
            email={user.email}
            size={100}
            rating="pg"
            default="retro"
            className="CustomAvatar-image"
            style={{ margin: "10px" }}
            protocol="https://"
            className={classes.avatar}
          />
        </Column>
        <Column
          className={classes.container}
          vertical="center"
          horizontal="end"
        >
          <CreateButton label="Update Profile" />
        </Column>
      </Row>
    </div>
  );
}



export default ProfileSettings;
