import React, { useState, useEffect } from "react";
import axios from "axios";
import { Column, Row } from "simple-flexbox";
import { createUseStyles } from "react-jss";
import TasksComponent from "./AgreementComponent";
import CreateButton from "../../components/CreateButton"; 
import SLUGS from "../../resources/slugs";
import { convertSlugUrl } from "../../resources/utils";
import { useHistory, useLocation } from "react-router-dom";
import { get } from "react-hook-form";

const useStyles = createUseStyles({
    buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },
  tasks: {
    marginTop: 0,
    "@media (max-width: 1024px)": {
      marginTop: 30,
    },
  },
  buttonStyle: {
    display: "flex",
    justifyContent: "flex-end",
  },
});



function DashboardComponent() {

  const { push } = useHistory();
  let location = useLocation();
  
  const [items, setItems] = useState([]);
  
  useEffect(() => {
    getCurrentUser();
    //getItems();
  }, []);

  
  const currUser = "http://localhost:8000/api/user";

  const token = sessionStorage.getItem("xrsf");

  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };
  
  let id = "";

  const getCurrentUser = async () => {
    const data = await axios
      .get(currUser, config)
      .then((res) => {
        console.log(res);
        id = res.data.id; 
        axios
          .get(`http://localhost:8000/api/agreements/${id}`)
          .then((res) => {
            //console.log(res.data)
            setItems(res.data);
          })
          .catch((err) => {
            console.log(err);
          });
      })
      .catch((err) => {
        console.log(err);
        localStorage.removeItem("xrsf");
      });
  };

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  const classes = useStyles();
  return (
    <Column>
      <Row
        horizontal="end"
        className={classes.buttonRow}
      >
        <CreateButton
          label="Create Agreement"
          onClick={() => onClick(SLUGS.selectTemplate + `${id}`)}
        />
      </Row>
      <Row
        horizontal="space-between"
        className={classes.lastRow}
      >
        <TasksComponent
          containerStyles={classes.tasks}
          items={items}
        />
      </Row>
    </Column>
  );
}

export default DashboardComponent;
