import React from 'react';
import { Column } from 'simple-flexbox';
import { StyleSheet, css } from 'aphrodite';
import LogoComponent from './Logo';
import IconOverview from '../../assets/icon-overview';
import MenuItemComponent from './MenuItem';

const styles = StyleSheet.create({
  container: {
    backgroundColor: "#363740",
    width: 255,
    paddingTop: 32,
  },
  menuItemList: {
    marginTop: 52,
  },
  separator: {
    borderTop: "1px solid #DFE0EB",
    marginTop: 16,
    marginBottom: 16,
    opacity: 0.06,
  },
});

function SidebarComponent(props)
{
    return (
      <Column className={css(styles.container)}>
        <LogoComponent />
        <Column className={css(styles.menuItemList)}>
          <MenuItemComponent
            title="Agreements"
            icon={IconOverview}
            onClick={() => props.onChange("Agreements")}
            active={props.selectedItem === "Agreements"}
          />
          <MenuItemComponent
            title="Templates"
            icon={IconOverview}
            onClick={() => props.onChange("Templates")}
            active={props.selectedItem === "Templates"}
          />
          <MenuItemComponent
            title="Lawyers"
            icon={IconOverview}
            onClick={() => props.onChange("Lawyers")}
            active={props.selectedItem === "Lawyers"}
          />
          <MenuItemComponent
            title="Support Tickets"
            icon={IconOverview}
            onClick={() => props.onChange("Support-Tickets")}
            active={props.selectedItem === "Support-Tickets"}
          />
          <MenuItemComponent
            title="Settings"
            icon={IconOverview}
            onClick={() => props.onChange("Settings")}
            active={props.selectedItem === "Settings"}
          />
        </Column>
      </Column>
    );
}

export default SidebarComponent;
