import React, {useState, useEffect, useCallback} from 'react';
import { useForm, useController, Controller } from "react-hook-form";
import {
    Input, 
    FormGroup,
    Label,
    Button
} from "reactstrap";
import { Table1 } from "./Table";
import { NiceInput } from "./Input"; 
 
function ShareAgreement() {

    const method = useForm() 
    const { register, handleSubmit, setValue, control } = useForm();
    const [shareInfo, setShareInfo] = useState([]);
    const [founderInfo, setFounderInfo] = useState([]);
    const [investorInfo, setInvestorInfo] = useState([]);
    const [corpInfo, setCorpInfo] = useState([]); 
    const onSubmit = (data) => {
        alert(JSON.stringify(data, null, 2));
    }

    useEffect(() => {
        register({ name: "shareinfo" })
        register({ name: "founderinfo"})
    }, [register])
    
    const addShareRow = function(){
        let l = shareInfo.length + 1;
        const d = [...shareInfo];
            
        const item = {
          refNum: l,
          shareName: "",
          shareClass: "",
          shareType: "",
          shareTotal: 0,
        };
        d.push(item);
        setValue("shareinfo", d); 
        setShareInfo(d);
        
    }

    const addFounderRow = function () {
      let l = founderInfo.length + 1;
      const d = [...founderInfo];

      const item = {
        fname: "",
        refId:"",
        numHeld: 0,
        streetName: "",
        cityName:"",
        provinceName: "",
        postalCode: "",
        phone: "",
        email: "" 
      };
      d.push(item);
      setValue("founderinfo", d);
      setFounderInfo(d);
    };

    
    const removeShareRow = function(){
        let l = [...shareInfo];
        l.splice(-1, 1);

        setValue("shareinfo", l);
        setShareInfo(l);
            
        };
    
    const shareColumns = [
      {
        Header: "Reference Number",
        accessor: "refNum",
      },

      {
        Header: "Share Name",
        accessor: "shareName",
        Cell: ({ row: { index } }) => (
          <Input
            type="text"
            name={`shareinfo[${index}][shareName]`}
            innerRef={register}
          />
        ),
      },

      {
        Header: "Share Type",
        columns: [
          {
            Header: "Class",
            accessor: "shareClass",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`shareinfo[${index}][shareClass]`}
                innerRef={register}
              >
                <option>Class A</option>
                <option>Class B</option>
                <option>None</option>
              </Input>
            )
          },
          {
            Header: "Type",
            accessor: "shareType",
            Cell: ({ row: { index } }) => (
              <Input
                type="select"
                name={`shareinfo[${index}][shareType]`}
                innerRef={register}
              >
                <option>Common</option>
                <option>Preferred</option>
              </Input>
            )
          }
        ],
      },

      {
        Header: "Total Shares",
        accessor: "shareTotal",
        Cell: ({ row: { index } }) => (
          <Input
            type="number"
            name={`shareinfo[${index}][shareTotal]`}
            innerRef={register}
          />
        ),
      },
    ];

    const founderColumns = [
      {
        Header: "Full Name",
        accessor: "fname",
        Cell: ({ row: { index } }) => (
          <Input
            type="text"
            name={`founderinfo[${index}][fname]`}
            innerRef={register}
          />
        ),
      },

      {
        Header: "Shares Held",
        columns: [
          {
            Header: "Reference #",
            accessor: "refID",
          },

          {
            Header: "# Held",
            accessor: "numHeld",
            Cell: ({ row: { index } }) => (
              <Input
                type="number"
                name={`founderinfo[${index}][numHeld]`}
                innerRef={register}
              />
            ),
          },
        ],
      },

      {
        Header: "Address",
        columns: [
          {
            Header: "Street Name",
            accessor: "streetName",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][streetName]`}
                innerRef={register}
              />
            ),
          },
          {
            Header: "City",
            accessor: "cityName",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][cityName]`}
                innerRef={register}
              />
            ),
          },
          {
            Header: "Province",
            accessor: "provinceName",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][provinceName]`}
                innerRef={register}
              />
            ),
          },
          {
            Header: "Postal Code",
            accessor: "postalCode",
            Cell: ({ row: { index } }) => (
              <Input
                type="text"
                name={`founderinfo[${index}][postalCode]`}
                innerRef={register}
              />
            ),
          },
        ],
      },

      {
        Header: "Phone Number",
        accessor: "phone",
      },

      {
        Header: "Email(Optional)",
        accessor: "email",
      },
    ];
    return (
      <form onSubmit={handleSubmit(onSubmit)}>
        <FormGroup>
          <label>What is your company’s Name?</label>
          <Input type="text" name="companyName" innerRef={register} />
        </FormGroup>
        <FormGroup>
          <label>When was your company incorporated?</label>
          <Input type="date" name="companyDate" innerRef={register} />
        </FormGroup>
        <FormGroup>
          <Label for="table1">What Does Your Company Have?</Label>
          <Table1 columns={shareColumns} data={shareInfo} />
          <Button onClick={addShareRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup>
        <FormGroup>
          <Label for="table2">Who are the founders?</Label>
          <Table1 columns={founderColumns} data={founderInfo} />
          <Button onClick={addFounderRow}>Add Row </Button>{" "}
          <Button onClick={removeShareRow}>Remove Row</Button>
        </FormGroup>
        <input type="submit" />
      </form>
    );
}

export default ShareAgreement;
