import React, { useState, useEffect } from "react";
import axios from "axios";
import { Column, Row } from "simple-flexbox";
import { createUseStyles } from "react-jss";
import TasksComponent from "./AgreementComponent";
import CreateButton from "../../components/CreateButton"; 
import SLUGS from "../../resources/slugs";
import { convertSlugUrl } from "../../resources/utils";
import { useHistory } from "react-router-dom";
import { getUser } from "../../resources/server";

const useStyles = createUseStyles({
    buttonRow: {
    marginTop: 10,
    "@media (max-width: 768px)": {
      marginTop: 0,
    },
  },
  lastRow: {
    marginTop: 30,
  },
  tasks: {
    flexGrow: 1,
    marginTop: 0,
    "@media (max-width: 1024px)": {
      marginTop: 30,
    },
  },
  buttonStyle: {
    display: "flex",
    justifyContent: "flex-end",
  },
});



function DashboardComponent() {

  const { push } = useHistory();
  
  const [items, setItems] = useState([]);
  const currData = []; 
  useEffect(() => {
    getItems();
  }, []);

  const token = sessionStorage.getItem("xrsf");
  const config = {
    headers: { Authorization: `Bearer ${token}` },
  };

  const getItems = async () => {
    const data = await axios
      .get(`http://localhost:8000/api/agreements`, config)
      .then((res) => {
        setItems(res.data.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  function onClick(slug, parameters = {}) {
    push(convertSlugUrl(slug, parameters));
  }

  const classes = useStyles();
  return (
    <Column>
      <Row
        horizontal="end"
        className={classes.buttonRow}
      >
        <CreateButton
          label="Create Agreement"
          onClick={() => onClick(SLUGS.selectTemplate)}
        />
      </Row>
      <Column
        horizontal="space-between"
        className={classes.lastRow}
      >
        <TasksComponent
          containerStyles={classes.tasks}
          items={items}
        />
      </Column>
    </Column>
  );
}

export default DashboardComponent;
