import React from "react";
import { useTable } from "react-table";
import {Input} from "reactstrap"
export const Table1 = React.forwardRef(({ columns, data, methods}, ref) => {
  // Use the state and functions returned from useTable to build your UI
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data,
      //getRowId: React.useCallback((row) => row[rowKey], [rowKey]),
    });

  // Render the UI for your table
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
            prepareRow(row);
            console.log(columns); 
            return (
            
            <tr {...row.getRowProps()}>
              <td>
                <Input
                  type="text"
                  name={`shareInfo[RefNum]`}
                  placeholder={columns[0]["accessor"]}
                  innerRef={methods.register}
                />
              </td>
              <td>
                <Input
                  type="text"
                  name={`shareInfo[shareName]`}
                  placeholder={columns[1]["accessor"]}
                  innerRef={methods.register}
                />
              </td>
              <td>
                <Input
                  type="select"
                  name={`shareInfo[shareClass]`}
                  innerRef={methods.register}
                >
                  <option>Class A</option>
                  <option>Class B</option>
                  <option>None</option>
                </Input>
              </td>
              <td>
                <Input
                  type="select"
                  name={`shareInfo[shareType]`}
                  innerRef={methods.register}
                >
                  <option>Common</option>
                  <option>Preferred</option>
                </Input>
              </td>

              <td>
                <Input
                  type="number"
                  name={`shareInfo[shareTotal]`}
                  placeholder={columns["shareTotal"]}
                  innerRef={methods.register}
                />
              </td>
            </tr>
          );
        })}
      </tbody>
    </table>
  );
});
export default function Table({ columns, data, ...rest }) {
  // Use the state and functions returned from useTable to build your UI
  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } =
    useTable({
      columns,
      data,
    });

  // Render the UI for your table
  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map((cell) => {
                return <td {...cell.getCellProps()}>{cell.render("Cell")}</td>;
              })}
            </tr>
          );
        })}
      </tbody>
    </table>
  );
}
