import React, {ComponentProps} from "react";
import { Input } from "reactstrap";
import { InputType } from "reactstrap";
import { useController, useForm } from "react-hook-form";

// export default function NiceInput({ control, name}) {
//   const {
//     field: { ref, ...inputProps },
//     fieldState: { invalid, isTouched, isDirty },
//     formState: { touchedFields, dirtyFields },
//   } = useController({
//     name,
//     control,
//     rules: { required: true },
//     defaultValue: "",
//   });

//   return <Input {...inputProps} />;
// }

export const NiceInput = React.forwardRef(({ name, control, type, method }, ref) => {
  const {
    field: { refs, ...inputProps },
  } = useController({
    name,
    control,
    //rules: { required: true },
    defaultValue: "",
  });

  return <Input name={name} type={type} innerRef={method} />;
});

export const SelectNiceInput = React.forwardRef(({ name, options, onChange, innerRef }) => {

  return (<Input
    name={name}
    onChange={onChange}
    innerRef={innerRef}
    type="select"
    ref={refs}>
    {options.map(value => (
      <options key={value} value={value}>
        {value}
      </options>
    ))}
  </Input>
  );
});
